import React, { useMemo, useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Switch } from "./ui/switch";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "./ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "./ui/table";
import {
  Play,
  Square,
  Pencil,
  Trash2,
  Plus,
  Video,
  Activity,
  Server,
  ChevronLeft,
  ChevronRight,
  Eye, // ‚úÖ NUEVO √çCONO: Para el bot√≥n de "Ver en vivo"
} from "lucide-react";
import { toast } from "sonner";

interface Camera {
  id: string;
  name: string;
  rtsp_url: string;
  enabled: boolean;
  fps_target: number;
  infer_every_n_frames: number;
  status: "RUNNING" | "STOPPED";
}

const initialCameras: Camera[] = [
  {
    id: "0", // üî¥ OJO: Cambi√© temporalmente el ID a "0" para que funcione con tu c√°mara web local en las pruebas
    name: "Webcam Local (Pruebas)",
    rtsp_url: "0",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "RUNNING",
  },
  {
    id: "CAM-002",
    name: "Estacionamiento Norte",
    rtsp_url: "rtsp://192.168.1.101:554/stream1",
    enabled: true,
    fps_target: 25,
    infer_every_n_frames: 10,
    status: "RUNNING",
  },
  {
    id: "CAM-003",
    name: "Recepci√≥n Principal",
    rtsp_url: "rtsp://192.168.1.102:554/stream1",
    enabled: false,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "STOPPED",
  },
];

export function Cameras() {
  const [cameras, setCameras] = useState<Camera[]>(initialCameras);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  
  // ‚úÖ NUEVOS ESTADOS PARA EL MONITOR DE VIDEO EN VIVO
  const [isVideoModalOpen, setIsVideoModalOpen] = useState(false);
  const [activeVideoCamera, setActiveVideoCamera] = useState<Camera | null>(null);

  const [editingCamera, setEditingCamera] = useState<Camera | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    rtsp_url: "",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
  });

  // PAGINACI√ìN
  const pageSize = 5;
  const [page, setPage] = useState(1);
  const totalPages = Math.max(1, Math.ceil(cameras.length / pageSize));

  useEffect(() => {
    setPage((p) => Math.min(p, totalPages));
  }, [totalPages]);

  const pageData = useMemo(() => {
    const start = (page - 1) * pageSize;
    return cameras.slice(start, start + pageSize);
  }, [cameras, page]);

  const canPrev = page > 1;
  const canNext = page < totalPages;

  const goPrev = () => setPage((p) => Math.max(1, p - 1));
  const goNext = () => setPage((p) => Math.min(totalPages, p + 1));

  const handleStartStop = (id: string, currentStatus: string) => {
    setCameras((prev) =>
      prev.map((cam) =>
        cam.id === id
          ? {
              ...cam,
              status:
                currentStatus === "RUNNING"
                  ? "STOPPED"
                  : ("RUNNING" as "RUNNING" | "STOPPED"),
            }
          : cam
      )
    );
    toast.success(
      currentStatus === "RUNNING"
        ? "üî¥ C√°mara detenida exitosamente"
        : "üü¢ C√°mara iniciada exitosamente"
    );
  };

  const handleDelete = (id: string, name: string) => {
    setCameras((prev) => prev.filter((cam) => cam.id !== id));
    toast.success(`üóëÔ∏è C√°mara "${name}" eliminada exitosamente`);
  };

  const handleEdit = (camera: Camera) => {
    setEditingCamera(camera);
    setFormData({
      name: camera.name,
      rtsp_url: camera.rtsp_url,
      enabled: camera.enabled,
      fps_target: camera.fps_target,
      infer_every_n_frames: camera.infer_every_n_frames,
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingCamera) {
      setCameras((prev) =>
        prev.map((cam) =>
          cam.id === editingCamera.id ? { ...cam, ...formData } : cam
        )
      );
      toast.success(`‚úÖ C√°mara "${formData.name}" actualizada exitosamente`);
    } else {
      const newCamera: Camera = {
        id: `CAM-${String(cameras.length + 1).padStart(3, "0")}`,
        ...formData,
        status: formData.enabled ? "RUNNING" : "STOPPED",
      };
      setCameras((prev) => [...prev, newCamera]);
      toast.success(`‚úÖ C√°mara "${formData.name}" creada exitosamente`);
    }
    setIsDialogOpen(false);
  };

  const openCreateDialog = () => {
    setEditingCamera(null);
    setFormData({
      name: "",
      rtsp_url: "",
      enabled: true,
      fps_target: 30,
      infer_every_n_frames: 5,
    });
    setIsDialogOpen(true);
  };

  // ‚úÖ NUEVA FUNCI√ìN: Abrir el modal de video
  const handleOpenVideo = (camera: Camera) => {
    setActiveVideoCamera(camera);
    setIsVideoModalOpen(true);
  };

  const runningCameras = cameras.filter((c) => c.status === "RUNNING").length;
  const stoppedCameras = cameras.filter((c) => c.status === "STOPPED").length;

  return (
    <div className="space-y-5">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-slate-900">Gesti√≥n de C√°maras</h1>
          <p className="text-sm text-slate-600 mt-1">
            Administra las c√°maras IP conectadas al sistema de detecci√≥n
          </p>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button
              onClick={openCreateDialog}
              className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 shadow-md hover:shadow-lg transition-all"
              size="lg"
            >
              <Plus className="h-5 w-5 mr-2" />
              Nueva C√°mara
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[600px]">
             {/* ... (Todo el contenido de tu formulario original se mantiene intacto aqu√≠) ... */}
            <DialogHeader>
              <DialogTitle className="text-2xl flex items-center gap-2">
                <Video className="h-6 w-6 text-blue-600" />
                {editingCamera ? "Editar C√°mara" : "Crear Nueva C√°mara"}
              </DialogTitle>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-5 pt-4">
              <div className="space-y-2">
                <Label htmlFor="name" className="text-slate-700 font-semibold">
                  Nombre de la C√°mara
                </Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Ej: Entrada Principal"
                  className="bg-slate-50 border-slate-300 h-11"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="rtsp_url" className="text-slate-700 font-semibold">
                  URL RTSP
                </Label>
                <Input
                  id="rtsp_url"
                  value={formData.rtsp_url}
                  onChange={(e) => setFormData({ ...formData, rtsp_url: e.target.value })}
                  placeholder="rtsp://192.168.1.100:554/stream1"
                  className="bg-slate-50 border-slate-300 font-mono text-sm h-11"
                  required
                />
              </div>

              <div className="flex items-center justify-between p-5 bg-blue-50 rounded-xl border-2 border-blue-200">
                <div className="space-y-0.5">
                  <Label htmlFor="enabled" className="text-slate-900 font-semibold cursor-pointer">
                    Iniciar Autom√°ticamente
                  </Label>
                </div>
                <Switch
                  id="enabled"
                  checked={formData.enabled}
                  onCheckedChange={(checked) => setFormData({ ...formData, enabled: checked })}
                />
              </div>

              <DialogFooter className="gap-2 pt-4">
                <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)} className="h-11">
                  Cancelar
                </Button>
                <Button type="submit" className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 h-11 px-8">
                  {editingCamera ? "üíæ Actualizar" : "‚ûï Crear"} C√°mara
                </Button>
              </DialogFooter>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* ‚úÖ NUEVO: DI√ÅLOGO DEL MONITOR EN VIVO */}
      <Dialog open={isVideoModalOpen} onOpenChange={setIsVideoModalOpen}>
        <DialogContent className="sm:max-w-4xl bg-slate-950 border-slate-800 text-slate-100">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-slate-100">
              <div className="h-2 w-2 bg-rose-500 rounded-full animate-pulse" />
              MONITOR EN VIVO: {activeVideoCamera?.name}
            </DialogTitle>
          </DialogHeader>
          <div className="relative aspect-video bg-black rounded-lg overflow-hidden flex items-center justify-center border border-slate-800 mt-2">
            {/* üî¥ AQU√ç OCURRE LA MAGIA: Conexi√≥n con FastAPI */}
            {activeVideoCamera && (
              <img
                src={`http://localhost:8000/api/v1/inference/stream/${activeVideoCamera.id}`}
                alt={`Transmisi√≥n de ${activeVideoCamera.name}`}
                className="w-full h-full object-contain"
                onError={(e) => {
                  (e.target as HTMLImageElement).style.display = 'none';
                  toast.error("No se pudo conectar con el servidor de video.");
                }}
              />
            )}
            
            {/* Overlay est√©tico */}
            <div className="absolute top-4 right-4 bg-black/60 backdrop-blur-sm px-3 py-1.5 rounded-md font-mono text-xs text-white border border-white/10 flex items-center gap-2">
              <span className="text-emerald-400">FPS:</span> {activeVideoCamera?.fps_target}
            </div>
          </div>
        </DialogContent>
      </Dialog>


      {/* Stats Cards y Tabla */}
      {/* ... (Se mantiene igual a tu c√≥digo, solo agregamos el bot√≥n de Ver en Vivo en la tabla) ... */}
      
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        {/* Tus 4 cards estad√≠sticas van aqu√≠, se mantienen sin cambios */}
      </div>

      <Card className="border-0 shadow-sm">
        <CardHeader className="border-b bg-gradient-to-r from-slate-50 to-slate-100">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="bg-slate-200 p-2 rounded-lg">
                <Server className="h-5 w-5 text-slate-700" />
              </div>
              <div>
                <CardTitle className="text-lg">C√°maras Registradas</CardTitle>
              </div>
            </div>
          </div>
        </CardHeader>

        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow className="bg-slate-50/80 hover:bg-slate-50/80">
                  <TableHead className="font-bold text-xs text-slate-700">ID C√°mara</TableHead>
                  <TableHead className="font-bold text-xs text-slate-700">Nombre</TableHead>
                  <TableHead className="font-bold text-xs text-slate-700">Estado</TableHead>
                  <TableHead className="text-right font-bold text-xs text-slate-700">Acciones</TableHead>
                </TableRow>
              </TableHeader>

              <TableBody>
                {pageData.map((camera) => (
                  <TableRow key={camera.id} className="hover:bg-slate-50/80 transition-colors border-b border-slate-100">
                    <TableCell className="font-mono font-bold text-slate-900 text-sm">
                      {camera.id}
                    </TableCell>

                    <TableCell>
                      <div className="flex items-center gap-3">
                        <span className="font-semibold text-slate-900 text-sm">{camera.name}</span>
                      </div>
                    </TableCell>

                    <TableCell className="text-center">
                      <Badge
                        variant="outline"
                        className={`font-bold text-xs px-3 py-1.5 inline-flex items-center gap-2 ${
                          camera.status === "RUNNING"
                            ? "bg-emerald-100 text-emerald-700 border-emerald-300"
                            : "bg-slate-100 text-slate-600 border-slate-300"
                        }`}
                      >
                        <span
                          className={`h-2 w-2 rounded-full ${
                            camera.status === "RUNNING" ? "bg-emerald-500 animate-pulse" : "bg-slate-400"
                          }`}
                        />
                        {camera.status}
                      </Badge>
                    </TableCell>

                    <TableCell>
                      <div className="flex items-center justify-end gap-2">
                        
                        {/* ‚úÖ NUEVO BOT√ìN: Ver en Vivo (Solo habilitado si est√° en RUNNING) */}
                        <Button
                          size="sm"
                          onClick={() => handleOpenVideo(camera)}
                          disabled={camera.status === "STOPPED"}
                          className={`${
                            camera.status === "RUNNING"
                              ? "bg-blue-600 hover:bg-blue-700 text-white shadow-sm"
                              : "bg-slate-200 text-slate-400 cursor-not-allowed"
                          } h-8 px-3 text-xs`}
                        >
                          <Eye className="h-4 w-4 mr-1.5" />
                          Ver en Vivo
                        </Button>

                        {/* Botones originales de Start/Stop, Edit y Delete */}
                        {camera.status === "STOPPED" ? (
                          <Button size="sm" onClick={() => handleStartStop(camera.id, camera.status)} className="bg-emerald-600 hover:bg-emerald-700 text-white shadow-sm h-8 px-3 text-xs">
                            <Play className="h-4 w-4 mr-1.5" /> Iniciar
                          </Button>
                        ) : (
                          <Button size="sm" variant="outline" onClick={() => handleStartStop(camera.id, camera.status)} className="border-rose-300 text-rose-700 hover:bg-rose-50 hover:border-rose-400 h-8 px-3 text-xs">
                            <Square className="h-4 w-4 mr-1.5" /> Detener
                          </Button>
                        )}

                        <Button size="sm" variant="outline" onClick={() => handleEdit(camera)} className="hover:bg-blue-50 hover:text-blue-700 hover:border-blue-300 h-8 px-2">
                          <Pencil className="h-4 w-4" />
                        </Button>
                        <Button size="sm" variant="outline" onClick={() => handleDelete(camera.id, camera.name)} className="hover:bg-rose-50 hover:text-rose-700 hover:border-rose-300 h-8 px-2">
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
