import React, { useEffect, useMemo, useState } from "react";
import { Card, CardContent } from "./ui/card";
import { Badge } from "./ui/badge";
import { Button } from "./ui/button";
import { Download, Image as ImageIcon, RefreshCw, X, Calendar, Camera } from "lucide-react";

// ✅ API base compatible con CRA (REACT_APP_*) y Vite (VITE_*)
const API_BASE =
  (process as any).env?.REACT_APP_API_BASE ||
  (import.meta as any).env?.VITE_API_URL ||
  "http://localhost:8000/api/v1";

const PUBLIC_BASE =
  (process as any).env?.REACT_APP_PUBLIC_BASE_URL ||
  "http://localhost:8000";

// -------- Types según tu backend de incidents ----------
type IncidentDoc = {
  _id: string;
  weapon_type?: string;
  confidence?: number;
  evidence_url?: string | null;
  camera_id?: string | null;
  timestamp?: string; // iso
};

function isHttp(url: string) {
  return /^https?:\/\//i.test(url);
}

function resolveEvidenceUrl(url?: string | null) {
  if (!url) return "";
  if (isHttp(url)) return url;
  // Si tu evidence_url viene como "/static/....jpg"
  if (url.startsWith("/")) return `${PUBLIC_BASE}${url}`;
  // Si viene como "static/....jpg"
  return `${PUBLIC_BASE}/${url}`;
}

function formatDateTime(iso?: string) {
  if (!iso) return { date: "—", time: "—" };
  const d = new Date(iso);
  if (Number.isNaN(d.getTime())) return { date: "—", time: "—" };
  const date = d.toLocaleDateString();
  const time = d.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit", second: "2-digit" });
  return { date, time };
}

function severityFromConfidence(conf: number) {
  if (conf >= 0.9) return { label: "ALTA", cls: "bg-emerald-600 text-white" };
  if (conf >= 0.75) return { label: "MEDIA", cls: "bg-amber-600 text-white" };
  return { label: "BAJA", cls: "bg-rose-600 text-white" };
}

export function Evidence() {
  const [items, setItems] = useState<IncidentDoc[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const [selected, setSelected] = useState<IncidentDoc | null>(null);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(`${API_BASE}/incidents?limit=200`);
      if (!res.ok) throw new Error(`HTTP ${res.status}`);
      const data: IncidentDoc[] = await res.json();

      // ✅ Solo los que tienen evidencia
      const withEvidence = (data || []).filter((x) => !!x.evidence_url);
      setItems(withEvidence);
    } catch (e: any) {
      setError(e?.message || "Error cargando evidencias");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const stats = useMemo(() => {
    const total = items.length;
    const avg =
      total > 0
        ? Math.round((items.reduce((acc, it) => acc + (it.confidence || 0), 0) / total) * 100)
        : 0;

    // "Hoy"
    const today = new Date();
    const y = today.getFullYear();
    const m = today.getMonth();
    const d = today.getDate();
    const todayCount = items.filter((it) => {
      if (!it.timestamp) return false;
      const t = new Date(it.timestamp);
      return t.getFullYear() === y && t.getMonth() === m && t.getDate() === d;
    }).length;

    return { total, avg, todayCount };
  }, [items]);

  const downloadOne = async (it: IncidentDoc) => {
    const url = resolveEvidenceUrl(it.evidence_url);
    if (!url) return;

    // ✅ descarga “normal” abriendo el recurso
    const a = document.createElement("a");
    a.href = url;
    a.download = `evidence_${it._id}.jpg`;
    document.body.appendChild(a);
    a.click();
    a.remove();
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-3">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-slate-900">Galería de Evidencias</h1>
          <p className="text-slate-600 text-sm mt-1">Capturas visuales de todas las detecciones registradas</p>
        </div>

        <div className="flex items-center gap-2">
          <Button variant="outline" onClick={fetchData} disabled={loading} className="h-10">
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? "animate-spin" : ""}`} />
            Refrescar
          </Button>

          {/* “Descargar Todas” (simple): abre la primera en nuevas pestañas si quieres,
             o luego lo hacemos ZIP desde backend. Por ahora lo dejo como placeholder. */}
          <Button className="h-10" onClick={() => alert("Si quieres, hacemos un ZIP desde backend para Descargar Todas ✅")}>
            <Download className="h-4 w-4 mr-2" />
            Descargar Todas
          </Button>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card className="border-0 shadow-sm">
          <CardContent className="py-4 px-4 flex items-center justify-between">
            <div>
              <p className="text-xs font-semibold text-slate-600">Total de Evidencias</p>
              <p className="text-2xl font-bold text-slate-900 mt-1">{stats.total}</p>
            </div>
            <div className="bg-blue-100 p-2.5 rounded-xl">
              <ImageIcon className="h-5 w-5 text-blue-600" />
            </div>
          </CardContent>
        </Card>

        <Card className="border-0 shadow-sm">
          <CardContent className="py-4 px-4 flex items-center justify-between">
            <div>
              <p className="text-xs font-semibold text-slate-600">Hoy</p>
              <p className="text-2xl font-bold text-slate-900 mt-1">{stats.todayCount}</p>
            </div>
            <div className="bg-emerald-100 p-2.5 rounded-xl">
              <Calendar className="h-5 w-5 text-emerald-600" />
            </div>
          </CardContent>
        </Card>

        <Card className="border-0 shadow-sm">
          <CardContent className="py-4 px-4 flex items-center justify-between">
            <div>
              <p className="text-xs font-semibold text-slate-600">Confianza Promedio</p>
              <p className="text-2xl font-bold text-slate-900 mt-1">{stats.avg}%</p>
            </div>
            <div className="bg-amber-100 p-2.5 rounded-xl">
              <span className="font-black text-amber-700">AI</span>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Estado */}
      {error && (
        <div className="p-4 rounded-xl border border-rose-200 bg-rose-50 text-rose-700 font-semibold">
          Error: {error}
        </div>
      )}

      {(!loading && items.length === 0) && (
        <Card className="border-0 shadow-sm">
          <CardContent className="py-16 text-center">
            <div className="inline-flex p-4 bg-slate-100 rounded-full text-slate-400">
              <ImageIcon className="h-10 w-10" />
            </div>
            <p className="mt-4 font-bold text-slate-900">No hay evidencias</p>
            <p className="text-slate-600 text-sm mt-1">Aún no se han generado capturas de evidencia.</p>
          </CardContent>
        </Card>
      )}

      {/* Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {items.map((it) => {
          const conf = it.confidence ?? 0;
          const sev = severityFromConfidence(conf);
          const { date, time } = formatDateTime(it.timestamp);
          const img = resolveEvidenceUrl(it.evidence_url);

          return (
            <Card key={it._id} className="border-0 shadow-sm overflow-hidden hover:shadow-md transition-shadow">
              {/* ✅ Importante: object-cover en la card está bien,
                  pero en el MODAL usaremos object-contain para que NO recorte */}
              <div className="relative aspect-[16/9] bg-black">
                <img
                  src={img}
                  alt={`evidence_${it._id}`}
                  className="w-full h-full object-cover"
                  onClick={() => setSelected(it)}
                  style={{ cursor: "pointer" }}
                />
                <Badge className={`absolute top-2 right-2 ${sev.cls} shadow-sm`}>
                  {(conf * 100).toFixed(0)}%
                </Badge>
              </div>

              <CardContent className="p-4 space-y-3">
                <div className="flex items-center justify-between gap-2">
                  <p className="font-mono font-bold text-slate-900 text-sm truncate">{it._id}</p>
                  <Badge variant="outline" className="text-[11px] font-bold">
                    {it.weapon_type || "—"}
                  </Badge>
                </div>

                <div className="text-xs text-slate-600 space-y-1">
                  <div className="flex items-center gap-2">
                    <Calendar className="h-3.5 w-3.5" />
                    <span className="font-mono">{date} {time}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <Camera className="h-3.5 w-3.5" />
                    <span className="font-mono">{it.camera_id ?? "—"}</span>
                  </div>
                </div>

                <div className="flex gap-2">
                  <Button variant="outline" className="flex-1 h-9" onClick={() => setSelected(it)}>
                    Ver
                  </Button>
                  <Button className="flex-1 h-9" onClick={() => downloadOne(it)}>
                    <Download className="h-4 w-4 mr-2" />
                    Descargar
                  </Button>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Modal (✅ aquí va la solución de “no se ve completa”) */}
      {selected && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm">
          <div className="bg-white w-full max-w-5xl rounded-2xl overflow-hidden shadow-2xl relative">
            <button
              onClick={() => setSelected(null)}
              className="absolute top-3 right-3 h-10 w-10 rounded-full bg-slate-100 hover:bg-slate-200 flex items-center justify-center"
              aria-label="Cerrar"
            >
              <X className="h-5 w-5 text-slate-700" />
            </button>

            <div className="p-6 border-b">
              <p className="font-bold text-slate-900 text-lg">Evidencia</p>
              <p className="text-slate-600 text-sm font-mono">{selected._id}</p>
            </div>

            <div className="p-4">
              {/* ✅ clave: object-contain + max-height */}
              <div className="w-full bg-black rounded-xl overflow-hidden flex items-center justify-center">
                <img
                  src={resolveEvidenceUrl(selected.evidence_url)}
                  alt="Evidencia completa"
                  className="w-full h-auto object-contain"
                  style={{ maxHeight: "75vh" }}
                />
              </div>

              <div className="mt-4 flex gap-2">
                <Button variant="outline" className="flex-1 h-10" onClick={() => setSelected(null)}>
                  Cerrar
                </Button>
                <Button className="flex-1 h-10" onClick={() => downloadOne(selected)}>
                  <Download className="h-4 w-4 mr-2" />
                  Descargar
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
