import {
  LayoutDashboard,
  Camera,
  AlertTriangle,
  Image as ImageIcon,
  Bell,
  Settings,
  Shield,
} from "lucide-react";

import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarHeader,
  SidebarFooter,
} from "./ui/sidebar";

import { Badge } from "./ui/badge";
import { cn } from "./ui/utils";

interface AdminSidebarProps {
  activeModule: string;
  onModuleChange: (module: string) => void;
}

const menuItems = [
  {
    title: "Dashboard",
    icon: LayoutDashboard,
    id: "dashboard",
    badge: null,
    description: "Panel principal",
  },
  {
    title: "Cámaras",
    icon: Camera,
    id: "cameras",
    badge: "24",
    description: "Gestión de cámaras",
  },
  {
    title: "Incidentes",
    icon: AlertTriangle,
    id: "incidents",
    badge: "7",
    description: "Detecciones registradas",
  },
  {
    title: "Evidencias",
    icon: ImageIcon,
    id: "evidence",
    badge: null,
    description: "Galería de capturas",
  },
  {
    title: "Alertas",
    icon: Bell,
    id: "alerts",
    badge: "3",
    description: "Centro de alertas",
  },
  {
    title: "Configuración",
    icon: Settings,
    id: "settings",
    badge: null,
    description: "Parámetros del sistema",
  },
];

export function AdminSidebar({ activeModule, onModuleChange }: AdminSidebarProps) {
  return (
    <Sidebar
      className={cn(
        // ancho “bonito” tipo Figma
        "w-[320px] min-w-[320px]",
        // tokens
        "bg-sidebar text-sidebar-foreground border-r border-sidebar-border",
        // layout fijo
        "flex flex-col",
        // suavidad
        "shadow-sm",
      )}
    >
      {/* HEADER (fijo) */}
      <SidebarHeader className="shrink-0 border-b border-sidebar-border p-4 bg-gradient-to-br from-blue-600 via-blue-600 to-blue-700">
        <div className="flex items-center gap-3">
          <div className="bg-white/15 backdrop-blur-sm p-2.5 rounded-xl shadow-lg ring-1 ring-white/25">
            <Shield className="h-6 w-6 text-white" />
          </div>
          <div className="leading-tight">
            <h2 className="font-bold text-lg text-white">Sentinel AI</h2>
            <p className="text-xs text-blue-100 font-medium">
              Sistema de Detección Inteligente
            </p>
          </div>
        </div>
      </SidebarHeader>

      {/* CONTENT (scroll) */}
      <SidebarContent className="flex-1 overflow-y-auto py-4">
        <SidebarGroup>
          <SidebarGroupLabel className="px-4 text-xs uppercase tracking-wider text-muted-foreground font-bold">
            MÓDULOS DEL SISTEMA
          </SidebarGroupLabel>

          <SidebarGroupContent className="mt-3">
            <SidebarMenu>
              {menuItems.map((item) => {
                const isActive = activeModule === item.id;
                const Icon = item.icon;

                return (
                  <SidebarMenuItem key={item.id}>
                    <SidebarMenuButton
                      onClick={() => onModuleChange(item.id)}
                      isActive={isActive}
                      className={cn(
                        // contenedor “pill” como figma
                        "mx-3 rounded-2xl h-auto py-3 px-3 transition-all duration-200",
                        "focus-visible:ring-2 focus-visible:ring-ring/60 focus-visible:outline-none",
                        isActive
                          ? "bg-gradient-to-r from-sidebar-primary to-blue-700 text-sidebar-primary-foreground shadow-lg"
                          : "hover:bg-sidebar-accent text-sidebar-foreground",
                      )}
                    >
                      {/* icon pill */}
                      <div
                        className={cn(
                          "shrink-0 rounded-xl p-2.5 transition-colors",
                          isActive ? "bg-white/15" : "bg-muted",
                        )}
                      >
                        <Icon className={cn("h-4 w-4", isActive ? "text-white" : "text-foreground")} />
                      </div>

                      {/* text */}
                      <div className="min-w-0 flex-1 text-left">
                        <div className={cn("font-semibold text-sm leading-5", isActive && "text-white")}>
                          {item.title}
                        </div>
                        <div
                          className={cn(
                            "text-xs leading-4 truncate",
                            isActive ? "text-blue-100" : "text-muted-foreground",
                          )}
                        >
                          {item.description}
                        </div>
                      </div>

                      {/* badge */}
                      {item.badge && (
                        <Badge
                          className={cn(
                            "ml-auto font-bold text-xs px-2.5 py-1 rounded-full",
                            "border-0",
                            isActive
                              ? "bg-white/20 text-white"
                              : "bg-rose-100 text-rose-700",
                          )}
                        >
                          {item.badge}
                        </Badge>
                      )}
                    </SidebarMenuButton>
                  </SidebarMenuItem>
                );
              })}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>

      {/* FOOTER (fijo) */}
      <SidebarFooter className="shrink-0 border-t border-sidebar-border p-4 bg-gradient-to-b from-background to-muted/40">
        <div className="space-y-3">
          <div className="flex items-center justify-between rounded-xl border border-border bg-card px-3 py-2 shadow-sm">
            <div className="flex items-center gap-2">
              <span className="h-2 w-2 rounded-full bg-emerald-500 animate-pulse shadow-[0_0_18px_rgba(16,185,129,0.55)]" />
              <p className="text-xs font-semibold text-foreground">Sistema Operativo</p>
            </div>
          </div>

          <div className="text-center">
            <p className="text-xs text-muted-foreground font-medium">© 2026 Sentinel AI</p>
            <p className="text-xs text-muted-foreground">Versión 2.1.0</p>
          </div>
        </div>
      </SidebarFooter>
    </Sidebar>
  );
}
