import { useState } from 'react';
import { AdminSidebar } from './components/AdminSidebar';
import { Topbar } from './components/Topbar';
import { Dashboard } from './components/Dashboard';
import { Cameras } from './components/Cameras';
import { Incidents } from './components/Incidents';
import { Evidence } from './components/Evidence';
import { Alerts } from './components/Alerts';
import { Settings } from './components/Settings';
import { Toaster } from './components/ui/sonner';
import { SidebarProvider, SidebarInset } from './components/ui/sidebar';
import './styles/globals.css';

export default function App() {
  const [activeModule, setActiveModule] = useState('dashboard');

  const renderContent = () => {
    switch (activeModule) {
      case 'dashboard': return <Dashboard />;
      case 'cameras': return <Cameras />;
      case 'incidents': return <Incidents />;
      case 'evidence': return <Evidence />;
      case 'alerts': return <Alerts />;
      case 'settings': return <Settings />;
      default: return <Dashboard />;
    }
  };

  return (
    <SidebarProvider>
      {/* 1. ELIMINADO: El <div className="flex h-screen..."> que envolvía todo.
        SidebarProvider ya se encarga de la disposición (Layout).
      */}

      <AdminSidebar 
        activeModule={activeModule} 
        onModuleChange={setActiveModule} 
      />

      {/* 2. SidebarInset: Es el contenedor que se ajusta cuando el sidebar se abre/cierra.
        Aquí aplicamos el color de fondo y el overflow.
      */}
      <SidebarInset className="flex flex-col h-screen overflow-hidden bg-slate-50">
        
        {/* Asegúrate de que tu Topbar tenga el <SidebarTrigger /> dentro */}
        <Topbar />
        
        <main className="flex-1 overflow-auto">
          <div className="p-4 md:p-8 max-w-[1600px] mx-auto w-full">
            {renderContent()}
          </div>
        </main>
      </SidebarInset>

      <Toaster />
    </SidebarProvider>
  );
}
