import {
  LayoutDashboard,
  Camera,
  AlertTriangle,
  Image,
  Bell,
  Settings,
  Shield,
} from "lucide-react";

import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarHeader,
  SidebarFooter,
} from "../shared/ui/sidebar";

import { Badge } from "../shared/ui/badge";
import { useAlerts } from "../app/providers/alerts-context";

interface AdminSidebarProps {
  activeModule: string;
  onModuleChange: (module: string) => void;
}

export function AdminSidebar({ activeModule, onModuleChange }: AdminSidebarProps) {
  const { unreadCount } = useAlerts();

  const menuItems = [
    {
      title: "Dashboard",
      icon: LayoutDashboard,
      id: "dashboard",
      badge: null as string | null,
      description: "Panel principal",
    },
    {
      title: "Cámaras",
      icon: Camera,
      id: "cameras",
      badge: null,
      description: "Gestión de cámaras",
    },
    {
      title: "Incidentes",
      icon: AlertTriangle,
      id: "incidents",
      badge: null,
      description: "Detecciones registradas",
    },
    {
      title: "Evidencias",
      icon: Image,
      id: "evidence",
      badge: null,
      description: "Galería de capturas",
    },
    {
      title: "Alertas",
      icon: Bell,
      id: "alerts",
      badge: unreadCount > 0 ? String(unreadCount > 99 ? "99+" : unreadCount) : null,
      description: "Centro de alertas",
    },
    {
      title: "Configuración",
      icon: Settings,
      id: "settings",
      badge: null,
      description: "Parámetros del sistema",
    },
  ];

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Share+Tech+Mono&family=Rajdhani:wght@400;500;600;700&display=swap');

        .sentinel-sidebar {
          background: #020a14 !important;
          border-right: 1px solid rgba(0, 120, 255, 0.2) !important;
          position: relative;
          overflow: hidden;
          font-family: 'Rajdhani', sans-serif !important;
        }

        /* Subtle grid background */
        .sentinel-sidebar::before {
          content: '';
          position: absolute;
          inset: 0;
          background-image:
            linear-gradient(rgba(0,120,255,0.03) 1px, transparent 1px),
            linear-gradient(90deg, rgba(0,120,255,0.03) 1px, transparent 1px);
          background-size: 30px 30px;
          pointer-events: none;
          z-index: 0;
        }

        /* Glow on the right edge */
        .sentinel-sidebar::after {
          content: '';
          position: absolute;
          top: 0; right: 0;
          width: 1px;
          height: 100%;
          background: linear-gradient(180deg, transparent, rgba(0,120,255,0.5), transparent);
          pointer-events: none;
          z-index: 1;
        }

        /* ---- HEADER ---- */
        .s-header {
          position: relative;
          z-index: 2;
          padding: 20px 20px 18px;
          border-bottom: 1px solid rgba(0,100,255,0.2);
          background: rgba(0,20,50,0.6);
        }

        .s-logo-wrap {
          display: flex;
          align-items: center;
          gap: 12px;
        }

        .s-logo-icon {
          width: 44px; height: 44px;
          background: linear-gradient(135deg, rgba(0,80,200,0.4), rgba(0,120,255,0.2));
          border: 1px solid rgba(0,120,255,0.4);
          display: flex; align-items: center; justify-content: center;
          clip-path: polygon(0 0, calc(100% - 8px) 0, 100% 8px, 100% 100%, 8px 100%, 0 calc(100% - 8px));
          box-shadow: 0 0 16px rgba(0,100,255,0.2);
          flex-shrink: 0;
        }

        .s-logo-icon svg {
          color: #4da6ff;
          width: 22px; height: 22px;
        }

        .s-logo-title {
          font-size: 18px;
          font-weight: 700;
          color: #e0eeff;
          letter-spacing: 2px;
          line-height: 1;
          margin-bottom: 3px;
        }

        .s-logo-title span { color: #0078ff; }

        .s-logo-sub {
          font-family: 'Share Tech Mono', monospace;
          font-size: 9px;
          color: rgba(0,120,255,0.6);
          letter-spacing: 1.5px;
        }

        /* ---- STATUS BADGE ---- */
        .s-status {
          display: flex;
          align-items: center;
          gap: 6px;
          margin-top: 14px;
          padding: 6px 10px;
          background: rgba(0,200,120,0.06);
          border: 1px solid rgba(0,200,120,0.15);
          width: fit-content;
        }

        .s-status-dot {
          width: 6px; height: 6px;
          border-radius: 50%;
          background: #00c878;
          animation: sBlink 2s ease-in-out infinite;
        }

        @keyframes sBlink {
          0%, 100% { opacity: 1; box-shadow: 0 0 6px #00c878; }
          50% { opacity: 0.3; box-shadow: none; }
        }

        .s-status-text {
          font-family: 'Share Tech Mono', monospace;
          font-size: 9px;
          color: rgba(0,200,120,0.8);
          letter-spacing: 1.5px;
        }

        /* ---- CONTENT ---- */
        .s-content {
          position: relative;
          z-index: 2;
          padding: 20px 0;
          overflow-y: auto;
          overflow-x: hidden;
          flex: 1;
        }

        .s-group-label {
          font-family: 'Share Tech Mono', monospace !important;
          font-size: 9px !important;
          color: rgba(0,100,200,0.5) !important;
          letter-spacing: 2px !important;
          padding: 0 18px;
          margin-bottom: 12px;
        }

        /* ---- MENU ITEMS ---- */
        .s-menu {
          display: flex;
          flex-direction: column;
          gap: 4px;
          padding: 0 12px;
        }

        .s-menu-item {
          list-style: none;
        }

        .s-menu-btn {
          width: 100%;
          padding: 10px 14px;
          background: transparent;
          border: 1px solid transparent;
          cursor: pointer;
          display: flex;
          align-items: center;
          gap: 12px;
          transition: all 0.2s ease;
          position: relative;
          text-align: left;
          clip-path: polygon(0 0, calc(100% - 8px) 0, 100% 8px, 100% 100%, 0 100%);
        }

        .s-menu-btn:hover:not(.s-active) {
          background: rgba(0,80,180,0.12);
          border-color: rgba(0,100,200,0.2);
        }

        .s-menu-btn.s-active {
          background: linear-gradient(135deg, rgba(0,60,180,0.35), rgba(0,100,255,0.2));
          border-color: rgba(0,120,255,0.4);
          box-shadow: 0 0 16px rgba(0,80,255,0.12), inset 0 0 20px rgba(0,80,255,0.04);
        }

        /* Active left bar */
        .s-menu-btn.s-active::before {
          content: '';
          position: absolute;
          left: 0; top: 20%; bottom: 20%;
          width: 2px;
          background: linear-gradient(180deg, transparent, #0078ff, transparent);
        }

        .s-icon-box {
          width: 36px; height: 36px;
          display: flex; align-items: center; justify-content: center;
          flex-shrink: 0;
          background: rgba(0,40,100,0.5);
          border: 1px solid rgba(0,80,180,0.2);
          clip-path: polygon(0 0, calc(100% - 6px) 0, 100% 6px, 100% 100%, 6px 100%, 0 calc(100% - 6px));
          transition: all 0.2s;
        }

        .s-active .s-icon-box {
          background: rgba(0,80,200,0.3);
          border-color: rgba(0,140,255,0.4);
          box-shadow: 0 0 10px rgba(0,100,255,0.2);
        }

        .s-icon-box svg {
          width: 16px; height: 16px;
          color: rgba(60,120,200,0.7);
          transition: color 0.2s;
        }

        .s-active .s-icon-box svg {
          color: #4da6ff;
        }

        .s-text-block {
          flex: 1;
          min-width: 0;
        }

        .s-title-row {
          display: flex;
          align-items: center;
          justify-content: space-between;
          gap: 6px;
        }

        .s-item-title {
          font-size: 13px;
          font-weight: 700;
          color: rgba(160,200,255,0.6);
          letter-spacing: 0.5px;
          transition: color 0.2s;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
        }

        .s-active .s-item-title {
          color: #c8deff;
        }

        .s-item-desc {
          font-family: 'Share Tech Mono', monospace;
          font-size: 9px;
          color: rgba(60,100,160,0.5);
          letter-spacing: 0.5px;
          margin-top: 2px;
          transition: color 0.2s;
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
        }

        .s-active .s-item-desc {
          color: rgba(100,160,255,0.6);
        }

        /* Badge */
        .s-badge {
          font-family: 'Share Tech Mono', monospace !important;
          font-size: 9px !important;
          font-weight: 700 !important;
          padding: 2px 7px !important;
          border-radius: 0 !important;
          background: rgba(255,50,80,0.15) !important;
          color: #ff6680 !important;
          border: 1px solid rgba(255,50,80,0.25) !important;
          letter-spacing: 1px;
          flex-shrink: 0;
        }

        .s-active .s-badge {
          background: rgba(0,120,255,0.2) !important;
          color: #80c0ff !important;
          border-color: rgba(0,150,255,0.3) !important;
        }

        /* Divider */
        .s-divider {
          height: 1px;
          margin: 16px 18px;
          background: linear-gradient(90deg, transparent, rgba(0,100,200,0.2), transparent);
        }

        /* ---- FOOTER ---- */
        .s-footer {
          position: relative;
          z-index: 2;
          padding: 14px 16px;
          border-top: 1px solid rgba(0,100,200,0.2);
          background: rgba(0,10,24,0.8);
        }

        .s-footer-inner {
          padding: 10px 14px;
          background: rgba(0,20,50,0.6);
          border: 1px solid rgba(0,80,180,0.2);
          clip-path: polygon(0 0, calc(100% - 8px) 0, 100% 8px, 100% 100%, 8px 100%, 0 calc(100% - 8px));
          margin-bottom: 10px;
        }

        .s-footer-sys {
          display: flex;
          align-items: center;
          gap: 6px;
        }

        .s-footer-dot {
          width: 5px; height: 5px;
          border-radius: 50%;
          background: #00c878;
          animation: sBlink 2s ease-in-out infinite;
          flex-shrink: 0;
        }

        .s-footer-sys-text {
          font-size: 11px;
          font-weight: 700;
          color: rgba(160,210,255,0.7);
          letter-spacing: 0.5px;
        }

        .s-footer-copy {
          text-align: center;
        }

        .s-footer-copy p {
          font-family: 'Share Tech Mono', monospace;
          font-size: 9px;
          color: rgba(40,80,140,0.6);
          letter-spacing: 1px;
        }
      `}</style>

      <Sidebar className="sentinel-sidebar border-r h-svh">
        {/* HEADER */}
        <SidebarHeader className="s-header p-0">
          <div className="s-logo-wrap">
            <div className="s-logo-icon">
              <Shield />
            </div>
            <div style={{ minWidth: 0 }}>
              <div className="s-logo-title">SENTINEL <span>AI</span></div>
              <div className="s-logo-sub">SISTEMA DE SEGURIDAD</div>
            </div>
          </div>
          <div className="s-status">
            <div className="s-status-dot" />
            <span className="s-status-text">SISTEMA ACTIVO</span>
          </div>
        </SidebarHeader>

        {/* CONTENT */}
        <SidebarContent className="s-content">
          <SidebarGroup>
            <SidebarGroupLabel className="s-group-label">
              MÓDULOS DEL SISTEMA
            </SidebarGroupLabel>

            <SidebarGroupContent>
              <SidebarMenu className="s-menu">
                {menuItems.map((item) => {
                  const Icon = item.icon;
                  const isActive = activeModule === item.id;

                  return (
                    <SidebarMenuItem key={item.id} className="s-menu-item">
                      <SidebarMenuButton
                        onClick={() => onModuleChange(item.id)}
                        isActive={isActive}
                        className={`s-menu-btn ${isActive ? "s-active" : ""}`}
                      >
                        <div className="s-icon-box">
                          <Icon />
                        </div>

                        <div className="s-text-block">
                          <div className="s-title-row">
                            <span className="s-item-title">{item.title}</span>
                            {item.badge && (
                              <Badge className="s-badge">{item.badge}</Badge>
                            )}
                          </div>
                          <p className="s-item-desc">{item.description}</p>
                        </div>
                      </SidebarMenuButton>
                    </SidebarMenuItem>
                  );
                })}
              </SidebarMenu>

              <div className="s-divider" />
            </SidebarGroupContent>
          </SidebarGroup>
        </SidebarContent>

        {/* FOOTER */}
        <SidebarFooter className="s-footer">
          <div className="s-footer-inner">
            <div className="s-footer-sys">
              <div className="s-footer-dot" />
              <span className="s-footer-sys-text">Sistema Operativo</span>
            </div>
          </div>
          <div className="s-footer-copy">
            <p>© 2026 SENTINEL AI</p>
            <p style={{ marginTop: 2 }}>VERSIÓN 2.1.0</p>
          </div>
        </SidebarFooter>
      </Sidebar>
    </>
  );
}
