import React, {
  createContext,
  useContext,
  useEffect,
  useMemo,
  useRef,
  useState,
  useCallback,
} from "react";
import { http } from "../../api/http";
import { useAuth } from "../../context/auth-context";

type Severity = "critical" | "high" | "medium" | "low";

export interface AlertApi {
  _id: string;
  type?: string;
  title: string;
  message: string;
  severity: Severity;
  weapon_type?: string | null;
  confidence?: number | null;
  evidence_url?: string | null;
  camera_id?: string | null;
  timestamp?: string | null;
  read?: boolean;
}

type AlertsContextValue = {
  alerts: AlertApi[];
  unreadCount: number;
  refresh: () => Promise<void>;
  markAsRead: (id: string) => Promise<void>;
  markAllAsRead: () => Promise<void>;
  deleteAlert: (id: string) => Promise<void>; // solo admin
};

const AlertsContext = createContext<AlertsContextValue | null>(null);

function normalizeSeverity(s: any): Severity {
  const v = String(s ?? "medium").toLowerCase();
  if (v === "critical") return "critical";
  if (v === "high") return "high";
  if (v === "medium") return "medium";
  return "low";
}

function normalizeConfidence(c: any): number | null {
  if (c == null) return null;
  const n = Number(c);
  if (!Number.isFinite(n)) return null;
  const v = n > 1 ? n / 100 : n;
  return Math.max(0, Math.min(1, v));
}

function normalizeAlert(a: any): AlertApi {
  return {
    _id: String(a._id ?? a.id ?? ""),
    type: a.type ?? "ALERTA",
    title: a.title ?? "Detecci√≥n",
    message: a.message ?? "",
    severity: normalizeSeverity(a.severity),
    weapon_type: a.weapon_type ?? null,
    confidence: normalizeConfidence(a.confidence),
    evidence_url: a.evidence_url ?? null,
    camera_id: a.camera_id ?? null,
    timestamp: a.timestamp ?? a.created_at ?? null,
    read: !!a.read,
  };
}

export function AlertsProvider({ children }: { children: React.ReactNode }) {
  const { user } = useAuth();
  const isAdmin = user?.role === "admin";

  const [alerts, setAlerts] = useState<AlertApi[]>([]);
  const pollRef = useRef<number | null>(null);

  const refresh = useCallback(async () => {
    const { data } = await http.get(`/alerts?limit=200`);
    const arr = (Array.isArray(data) ? data : []).map(normalizeAlert);

    // ordena por timestamp desc si existe
    arr.sort((a: AlertApi, b: AlertApi) =>
      String(b.timestamp ?? "").localeCompare(String(a.timestamp ?? ""))
    );

    setAlerts(arr);
  }, []);

  const markAsRead = useCallback(async (id: string) => {
    // optimistic
    setAlerts((prev) => prev.map((a) => (a._id === id ? { ...a, read: true } : a)));
    try {
      await http.patch(`/alerts/${id}/read?read=true`);
    } catch (e) {
      // rollback
      setAlerts((prev) => prev.map((a) => (a._id === id ? { ...a, read: false } : a)));
      throw e;
    }
  }, []);

  const markAllAsRead = useCallback(async () => {
    const unread = alerts.filter((a) => !a.read);
    if (unread.length === 0) return;

    // optimistic
    setAlerts((prev) => prev.map((a) => ({ ...a, read: true })));

    try {
      await Promise.all(unread.map((a) => http.patch(`/alerts/${a._id}/read?read=true`)));
    } catch (e) {
      await refresh();
      throw e;
    }
  }, [alerts, refresh]);

  const deleteAlert = useCallback(
    async (id: string) => {
      if (!isAdmin) throw new Error("No tienes permisos para eliminar alertas");

      const prev = alerts;
      setAlerts((p) => p.filter((a) => a._id !== id));

      try {
        await http.delete(`/alerts/${id}`);
      } catch (e) {
        setAlerts(prev);
        throw e;
      }
    },
    [alerts, isAdmin]
  );

  const unreadCount = useMemo(() => alerts.filter((a) => !a.read).length, [alerts]);

  // ‚úÖ Polling: refresca alertas cada 3s cuando hay sesi√≥n
  useEffect(() => {
    if (!user) {
      setAlerts([]);
      if (pollRef.current) window.clearInterval(pollRef.current);
      pollRef.current = null;
      return;
    }

    refresh().catch(console.error);

    if (pollRef.current) window.clearInterval(pollRef.current);
    pollRef.current = window.setInterval(() => {
      refresh().catch(() => {});
    }, 3000); // üî• cada 3 segundos

    return () => {
      if (pollRef.current) window.clearInterval(pollRef.current);
      pollRef.current = null;
    };
  }, [user?._id, refresh]);

  return (
    <AlertsContext.Provider
      value={{
        alerts,
        unreadCount,
        refresh,
        markAsRead,
        markAllAsRead,
        deleteAlert,
      }}
    >
      {children}
    </AlertsContext.Provider>
  );
}

export function useAlerts() {
  const ctx = useContext(AlertsContext);
  if (!ctx) throw new Error("useAlerts debe usarse dentro de <AlertsProvider/>");
  return ctx;
}
