import {
  LayoutDashboard,
  Camera,
  AlertTriangle,
  Image,
  Bell,
  Settings,
  Shield,
} from "lucide-react";
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarHeader,
  SidebarFooter,
} from "./ui/sidebar";
import { Badge } from "./ui/badge";

interface AdminSidebarProps {
  activeModule: string;
  onModuleChange: (module: string) => void;
}

const menuItems = [
  {
    title: "Dashboard",
    icon: LayoutDashboard,
    id: "dashboard",
    badge: null,
    description: "Panel principal",
  },
  {
    title: "Cámaras",
    icon: Camera,
    id: "cameras",
    badge: "24",
    description: "Gestión de cámaras",
  },
  {
    title: "Incidentes",
    icon: AlertTriangle,
    id: "incidents",
    badge: "7",
    description: "Detecciones registradas",
  },
  {
    title: "Evidencias",
    icon: Image,
    id: "evidence",
    badge: null,
    description: "Galería de capturas",
  },
  {
    title: "Alertas",
    icon: Bell,
    id: "alerts",
    badge: "3",
    description: "Centro de alertas",
  },
  {
    title: "Configuración",
    icon: Settings,
    id: "settings",
    badge: null,
    description: "Parámetros del sistema",
  },
];

export function AdminSidebar({ activeModule, onModuleChange }: AdminSidebarProps) {
  return (
    <Sidebar className="border-r bg-white shadow-sm">
      <SidebarHeader className="border-b p-6 bg-gradient-to-br from-blue-600 via-blue-600 to-blue-700">
        <div className="flex items-center gap-3">
          <div className="bg-white/20 backdrop-blur-sm p-3 rounded-xl shadow-lg ring-2 ring-white/30">
            <Shield className="h-7 w-7 text-white" />
          </div>
          <div>
            <h2 className="font-bold text-xl text-white">Sentinel AI</h2>
            <p className="text-xs text-blue-100 font-medium">
              Sistema de Detección Inteligente
            </p>
          </div>
        </div>
      </SidebarHeader>

      <SidebarContent className="pt-6 pb-4">
        <SidebarGroup>
          <SidebarGroupLabel className="px-4 text-xs uppercase tracking-wider text-slate-500 font-bold mb-2">
            Módulos del Sistema
          </SidebarGroupLabel>

          <SidebarGroupContent className="mt-2">
            <SidebarMenu>
              {menuItems.map((item) => (
                <SidebarMenuItem key={item.id}>
                  <SidebarMenuButton
                    onClick={() => onModuleChange(item.id)}
                    isActive={activeModule === item.id}
                    className={`
                      mx-2 rounded-xl transition-all duration-200 h-auto py-3
                      ${
                        activeModule === item.id
                          ? "bg-gradient-to-r from-blue-600 to-blue-700 text-white shadow-lg hover:shadow-xl scale-[1.02]"
                          : "hover:bg-slate-50 text-slate-700 hover:translate-x-0.5"
                      }
                    `}
                    tooltip={item.description}
                  >
                    <div
                      className={`p-2 rounded-lg ${
                        activeModule === item.id ? "bg-white/20" : "bg-slate-100"
                      }`}
                    >
                      <item.icon className="h-5 w-5" />
                    </div>

                    <div className="flex-1 text-left">
                      <span className="font-semibold text-sm">{item.title}</span>
                      <p
                        className={`text-xs ${
                          activeModule === item.id
                            ? "text-blue-100"
                            : "text-slate-500"
                        }`}
                      >
                        {item.description}
                      </p>
                    </div>

                    {item.badge && (
                      <Badge
                        className={`ml-auto font-bold ${
                          activeModule === item.id
                            ? "bg-white/30 text-white border-0 shadow-sm"
                            : "bg-rose-100 text-rose-700 border-0"
                        }`}
                      >
                        {item.badge}
                      </Badge>
                    )}
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>

      <SidebarFooter className="border-t p-6 bg-gradient-to-br from-slate-50 to-slate-100">
        <div className="space-y-3">
          <div className="flex items-center justify-between p-3 bg-white rounded-lg border border-slate-200 shadow-sm">
            <div className="flex items-center gap-2">
              <div className="h-2.5 w-2.5 rounded-full bg-emerald-500 animate-pulse shadow-lg shadow-emerald-500/50" />
              <p className="text-xs text-slate-700 font-semibold">
                Sistema Operativo
              </p>
            </div>
          </div>

          <div className="text-center">
            <p className="text-xs text-slate-400 font-medium">© 2026 Sentinel AI</p>
            <p className="text-xs text-slate-400">Versión 2.1.0</p>
          </div>
        </div>
      </SidebarFooter>
    </Sidebar>
  );
}
