import { http } from "./http";

export type IncidentOut = {
  _id?: string;
  id?: string;

  weapon_type?: string;
  type?: string;

  confidence?: number;

  camera_id?: string;
  camera_name?: string;

  image_url?: string;
  evidence_url?: string;
  snapshot_url?: string;

  created_at?: string;
  timestamp?: string;
};

export async function listIncidents() {
  const { data } = await http.get<IncidentOut[]>("/incidents");
  return data;
}

export async function deleteAllIncidents() {
  const { data } = await http.delete("/incidents");
  return data;
}
