        /* Form */
        .form { display: flex; flex-direction: column; gap: 18px; }

        .field {
          animation: fadeUp 0.5s ease both;
        }
        .field:nth-child(1) { animation-delay: 0.25s; }
        .field:nth-child(2) { animation-delay: 0.35s; }
        .field:nth-child(3) { animation-delay: 0.45s; }

        .field-label {
          display: block;
          font-family: 'Share Tech Mono', monospace;
          font-size: 10px;
          color: rgba(0,120,255,0.7);
          letter-spacing: 2px;
          margin-bottom: 8px;
        }

        .input-wrap {
          position: relative;
        }

        .input-icon {
          position: absolute;
          left: 14px;
          top: 50%;
          transform: translateY(-50%);
          color: rgba(0,120,255,0.5);
          font-size: 14px;
          pointer-events: none;
          transition: color 0.2s;
        }

        .input-wrap.focused .input-icon {
          color: #0078ff;
        }

        .field-input {
          width: 100%;
          background: rgba(0,30,60,0.6);
          border: 1px solid rgba(0,80,180,0.25);
          color: #c8deff;
          font-family: 'Share Tech Mono', monospace;
          font-size: 13px;
          padding: 12px 14px 12px 40px;
          outline: none;
          transition: all 0.25s;
          letter-spacing: 0.5px;
        }

        .field-input::placeholder {
          color: rgba(80,120,180,0.4);
        }

        .field-input:focus {
          border-color: rgba(0,120,255,0.6);
          background: rgba(0,40,80,0.7);
          box-shadow: 0 0 0 3px rgba(0,100,255,0.08), inset 0 0 20px rgba(0,80,255,0.04);
        }

        /* Bottom line animation on focus */
        .input-line {
          position: absolute;
          bottom: 0; left: 0;
          height: 2px;
          width: 0;
          background: linear-gradient(90deg, #0050ff, #00aaff);
          transition: width 0.3s ease;
        }

        .input-wrap.focused .input-line {
          width: 100%;
        }

        /* Button */
        .btn {
          position: relative;
          width: 100%;
          padding: 14px;
          background: linear-gradient(135deg, #0045cc, #0070ff);
          border: none;
          color: #fff;
          font-family: 'Rajdhani', sans-serif;
          font-size: 15px;
          font-weight: 700;
          letter-spacing: 3px;
          cursor: pointer;
          overflow: hidden;
          transition: all 0.25s;
          clip-path: polygon(0 0, calc(100% - 10px) 0, 100% 10px, 100% 100%, 10px 100%, 0 calc(100% - 10px));
          margin-top: 4px;
        }

        .btn:hover:not(:disabled) {
          background: linear-gradient(135deg, #0050dd, #0085ff);
          box-shadow: 0 0 20px rgba(0,100,255,0.4), 0 8px 20px rgba(0,0,0,0.3);
          transform: translateY(-1px);
        }

        .btn:active:not(:disabled) {
          transform: translateY(0);
        }

        .btn:disabled {
          opacity: 0.7;
          cursor: not-allowed;
        }

        /* Shimmer on button */
        .btn::before {
          content: '';
          position: absolute;
          top: 0; left: -100%;
          width: 60%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.15), transparent);
          transform: skewX(-20deg);
          transition: left 0.5s;
        }

        .btn:hover::before {
          left: 150%;
        }

        .btn-loader {
          display: inline-block;
          width: 14px; height: 14px;
          border: 2px solid rgba(255,255,255,0.3);
          border-top-color: #fff;
          border-radius: 50%;
          animation: spin 0.7s linear infinite;
          vertical-align: middle;
          margin-right: 8px;
        }

        @keyframes spin {
          to { transform: rotate(360deg); }
        }

        /* Footer */
        .footer {
          margin-top: 28px;
          display: flex;
          align-items: center;
          justify-content: space-between;
          animation: fadeUp 0.5s ease 0.5s both;
        }

        .footer-text {
          font-family: 'Share Tech Mono', monospace;
          font-size: 10px;
          color: rgba(60,100,160,0.6);
          letter-spacing: 1px;
        }

        .status {
          display: flex;
          align-items: center;
          gap: 5px;
          font-family: 'Share Tech Mono', monospace;
          font-size: 10px;
          color: rgba(0,200,120,0.7);
          letter-spacing: 1px;
        }

        .status-dot {
          width: 5px; height: 5px;
          border-radius: 50%;
          background: #00c878;
          animation: blink 2s ease-in-out infinite;
        }
      `}</style>

