import { http } from "./http";

export type UserOut = {
  _id: string;
  name: string;
  email: string;
  role: "admin" | "operator";
  estado?: number; // 1 activo, 0 inactivo
  created_at?: string | null;
};

export type UserCreate = {
  name: string;
  email: string;
  password: string;
  role: "admin" | "operator";
  estado?: 0 | 1;
};

export type UserUpdate = {
  name?: string;
  email?: string;
  password?: string;
  role?: "admin" | "operator";
  estado?: 0 | 1;
};

export async function listUsers(limit = 500) {
  const { data } = await http.get<UserOut[]>(`/users?limit=${limit}`);
  return data;
}

export async function createUser(payload: UserCreate) {
  const { data } = await http.post<UserOut>(`/users`, payload);
  return data;
}

export async function updateUser(userId: string, payload: UserUpdate) {
  const { data } = await http.patch<UserOut>(`/users/${userId}`, payload);
  return data;
}

export async function setUserRole(userId: string, role: "admin" | "operator") {
  // el backend espera role como query param (así lo tienes)
  const { data } = await http.patch<UserOut>(`/users/${userId}/role`, null, {
    params: { role },
  });
  return data;
}

export async function setUserEstado(userId: string, estado: 0 | 1) {
  // ✅ el backend espera BODY {estado}
  const { data } = await http.patch<UserOut>(`/users/${userId}/estado`, {
    estado,
  });
  return data;
}

export async function deleteUser(userId: string) {
  const { data } = await http.delete(`/users/${userId}`);
  return data;
}
