import React, { useEffect, useMemo, useState } from "react";

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:8000";
const API_URL = `${BACKEND_URL}/api/v1`;
const WS_URL = `${BACKEND_URL.replace("http", "ws")}/ws/alertas`;

function formatTime(value) {
  if (!value) return "-";
  return new Date(value).toLocaleTimeString();
}

function formatConfidence(value) {
  const n = Number(value || 0);
  if (n > 1) return `${n.toFixed(1)}%`;
  return `${(n * 100).toFixed(1)}%`;
}

function imageUrl(evento) {
  if (!evento?.evidence_url) return null;
  if (evento.evidence_url.startsWith("http")) return evento.evidence_url;
  const path = evento.evidence_url.startsWith("/") ? evento.evidence_url : `/${evento.evidence_url}`;
  return `${BACKEND_URL}${path}`;
}

function App() {
  const [alertas, setAlertas] = useState([]);
  const [estadoConexion, setEstadoConexion] = useState("Conectando");
  const [ultimoEvento, setUltimoEvento] = useState(null);

  useEffect(() => {
    fetch(`${API_URL}/incidentes`)
      .then((res) => res.json())
      .then((data) => {
        const list = Array.isArray(data) ? data : [];
        setAlertas(list);
        setUltimoEvento(list[0] || null);
      })
      .catch(() => setAlertas([]));
  }, []);

  useEffect(() => {
    const socket = new WebSocket(WS_URL);
    socket.onopen = () => setEstadoConexion("Activo");
    socket.onmessage = (event) => {
      const alerta = JSON.parse(event.data);
      setAlertas((prev) => [alerta, ...prev]);
      setUltimoEvento(alerta);
    };
    socket.onclose = () => setEstadoConexion("Desconectado");
    return () => socket.close();
  }, []);

  const stats = useMemo(
    () => ({
      totalIncidentes: alertas.length,
      estado: estadoConexion,
    }),
    [alertas.length, estadoConexion]
  );

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <div>
          <h1 style={styles.title}>Sentinel AI</h1>
          <p style={styles.subtitle}>Deteccion de armas en tiempo real</p>
        </div>
        <div style={{ ...styles.badge, backgroundColor: estadoConexion === "Activo" ? "#27ae60" : "#c0392b" }}>
          {stats.estado}
        </div>
      </header>

      <div style={styles.mainContent}>
        <section style={styles.leftPanel}>
          <h2 style={styles.sectionTitle}>Ultima alerta</h2>
          {ultimoEvento ? (
            <div style={styles.alertBox}>
              <div style={styles.alertHeader}>Amenaza detectada</div>
              <div style={styles.alertBody}>
                <h3 style={styles.weaponTitle}>{ultimoEvento.weapon_type || "Arma detectada"}</h3>
                <div style={styles.imageContainer}>
                  {imageUrl(ultimoEvento) ? (
                    <img src={imageUrl(ultimoEvento)} alt="Evidencia" style={styles.evidenceImage} />
                  ) : (
                    <span style={styles.placeholderText}>Sin evidencia visual</span>
                  )}
                </div>
                <div style={styles.statsRow}>
                  <div style={styles.statItem}>
                    <span style={styles.statLabel}>Confianza</span>
                    <span style={styles.statValue}>{formatConfidence(ultimoEvento.confidence)}</span>
                  </div>
                  <div style={styles.statItem}>
                    <span style={styles.statLabel}>Hora</span>
                    <span style={styles.statValue}>{formatTime(ultimoEvento.timestamp)}</span>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div style={styles.safeState}>Sin alertas por el momento</div>
          )}
        </section>

        <section style={styles.rightPanel}>
          <h2 style={styles.sectionTitle}>Historial ({stats.totalIncidentes})</h2>
          <div style={styles.historyList}>
            {alertas.map((item) => (
              <div key={item._id || `${item.timestamp}-${item.weapon_type}`} style={styles.historyItem} onClick={() => setUltimoEvento(item)}>
                <div style={styles.historyIcon}>{Number(item.confidence || 0) >= 0.8 ? "ROJO" : "NARANJA"}</div>
                <div style={styles.historyInfo}>
                  <div style={styles.historyType}>{item.weapon_type || "Deteccion"}</div>
                  <div style={styles.historyTime}>{formatTime(item.timestamp)}</div>
                </div>
                <div style={styles.historyScore}>{formatConfidence(item.confidence)}</div>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
}

const styles = {
  container: { fontFamily: "Segoe UI, sans-serif", backgroundColor: "#1e272e", minHeight: "100vh", color: "white", padding: "20px" },
  header: { display: "flex", justifyContent: "space-between", alignItems: "center", borderBottom: "1px solid #485460", paddingBottom: "20px", marginBottom: "30px" },
  title: { margin: 0, fontSize: "24px" },
  subtitle: { margin: 0, fontSize: "14px", color: "#808e9b" },
  badge: { padding: "8px 15px", borderRadius: "20px", fontWeight: "bold", fontSize: "14px" },
  mainContent: { display: "grid", gridTemplateColumns: "1fr 1fr", gap: "24px" },
  sectionTitle: { borderLeft: "4px solid #0fbcf9", paddingLeft: "10px", marginBottom: "20px" },
  leftPanel: { minWidth: 0 },
  alertBox: { backgroundColor: "#e74c3c", borderRadius: "15px", overflow: "hidden" },
  alertHeader: { backgroundColor: "#c0392b", padding: "15px", textAlign: "center", fontSize: "22px", fontWeight: "bold" },
  alertBody: { padding: "24px", backgroundColor: "#ecf0f1", color: "#2c3e50", textAlign: "center" },
  weaponTitle: { fontSize: "28px", margin: "10px 0", textTransform: "uppercase" },
  imageContainer: { margin: "20px 0", backgroundColor: "#000", borderRadius: "10px", minHeight: "280px", display: "flex", justifyContent: "center", alignItems: "center" },
  evidenceImage: { maxWidth: "100%", maxHeight: "380px", objectFit: "contain" },
  placeholderText: { color: "#7f8c8d" },
  statsRow: { display: "flex", justifyContent: "space-around", marginTop: "16px" },
  statItem: { display: "flex", flexDirection: "column" },
  statLabel: { fontSize: "12px", color: "#7f8c8d", textTransform: "uppercase" },
  statValue: { fontSize: "20px", fontWeight: "bold" },
  safeState: { backgroundColor: "#2f3640", padding: "50px", borderRadius: "15px", textAlign: "center", color: "#7f8c8d", border: "2px dashed #485460" },
  rightPanel: { backgroundColor: "#2f3640", padding: "20px", borderRadius: "15px", minHeight: "400px" },
  historyList: { maxHeight: "600px", overflowY: "auto" },
  historyItem: { display: "flex", alignItems: "center", backgroundColor: "#353b48", padding: "12px", marginBottom: "10px", borderRadius: "8px", cursor: "pointer" },
  historyIcon: { fontSize: "12px", marginRight: "12px", minWidth: "58px" },
  historyInfo: { flex: 1 },
  historyType: { fontWeight: "bold" },
  historyTime: { fontSize: "12px", color: "#d2dae2" },
  historyScore: { fontWeight: "bold", backgroundColor: "#1e272e", padding: "5px 10px", borderRadius: "5px" },
};

export default App;
