import React, { useMemo, useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Switch } from "./ui/switch";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "./ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "./ui/table";
import {
  Play,
  Square,
  Pencil,
  Trash2,
  Plus,
  Video,
  Activity,
  Server,
  ChevronLeft,
  ChevronRight,
  Eye,
} from "lucide-react";
import { toast } from "sonner";

interface Camera {
  id: string;
  name: string;
  rtsp_url: string;
  enabled: boolean;
  fps_target: number;
  infer_every_n_frames: number;
  status: "RUNNING" | "STOPPED";
}

const initialCameras: Camera[] = [
  {
    id: "0",
    name: "Webcam Local (Pruebas)",
    rtsp_url: "0",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "RUNNING",
  },
  {
    id: "CAM-002",
    name: "Estacionamiento Norte",
    rtsp_url: "rtsp://192.168.1.101:554/stream1",
    enabled: true,
    fps_target: 25,
    infer_every_n_frames: 10,
    status: "RUNNING",
  },
  {
    id: "CAM-003",
    name: "Recepci칩n Principal",
    rtsp_url: "rtsp://192.168.1.102:554/stream1",
    enabled: false,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "STOPPED",
  },
];

export function Cameras() {
  const [cameras, setCameras] = useState<Camera[]>(initialCameras);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isVideoModalOpen, setIsVideoModalOpen] = useState(false);
  const [activeVideoCamera, setActiveVideoCamera] = useState<Camera | null>(null);
  const [editingCamera, setEditingCamera] = useState<Camera | null>(null);
  
  const [formData, setFormData] = useState({
    name: "",
    rtsp_url: "",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
  });

  // PAGINACI칍N
  const pageSize = 5;
  const [page, setPage] = useState(1);
  const totalPages = Math.max(1, Math.ceil(cameras.length / pageSize));

  useEffect(() => {
    setPage((p) => Math.min(p, totalPages));
  }, [totalPages]);

  const pageData = useMemo(() => {
    const start = (page - 1) * pageSize;
    return cameras.slice(start, start + pageSize);
  }, [cameras, page]);

  const canPrev = page > 1;
  const canNext = page < totalPages;
  const goPrev = () => setPage((p) => Math.max(1, p - 1));
  const goNext = () => setPage((p) => Math.min(totalPages, p + 1));

  const handleStartStop = (id: string, currentStatus: string) => {
    setCameras((prev) =>
      prev.map((cam) =>
        cam.id === id
          ? { ...cam, status: currentStatus === "RUNNING" ? "STOPPED" : "RUNNING" }
          : cam
      )
    );
    toast.success(currentStatus === "RUNNING" ? "游댮 Detenida" : "游릭 Iniciada");
  };

  const handleDelete = (id: string, name: string) => {
    setCameras((prev) => prev.filter((cam) => cam.id !== id));
    toast.success(`C치mara "${name}" eliminada`);
  };

  const handleEdit = (camera: Camera) => {
    setEditingCamera(camera);
    setFormData({
      name: camera.name,
      rtsp_url: camera.rtsp_url,
      enabled: camera.enabled,
      fps_target: camera.fps_target,
      infer_every_n_frames: camera.infer_every_n_frames,
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingCamera) {
      setCameras((prev) => prev.map((cam) => cam.id === editingCamera.id ? { ...cam, ...formData } : cam));
      toast.success("C치mara actualizada");
    } else {
      const newCamera: Camera = {
        id: `CAM-${String(cameras.length + 1).padStart(3, "0")}`,
        ...formData,
        status: formData.enabled ? "RUNNING" : "STOPPED",
      };
      setCameras((prev) => [...prev, newCamera]);
      toast.success("C치mara creada");
    }
    setIsDialogOpen(false);
  };

  const runningCameras = cameras.filter((c) => c.status === "RUNNING").length;
  const stoppedCameras = cameras.filter((c) => c.status === "STOPPED").length;

  return (
    <div className="space-y-5">
      {/* HEADER Y BOT칍N NUEVA C츼MARA */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-slate-900">Gesti칩n de C치maras</h1>
          <p className="text-sm text-slate-600">Monitoreo y administraci칩n de dispositivos</p>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={() => { setEditingCamera(null); setFormData({ name: "", rtsp_url: "", enabled: true, fps_target: 30, infer_every_n_frames: 5 }); }}>
              <Plus className="h-5 w-5 mr-2" /> Nueva C치mara
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[500px]">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <Video className="h-5 w-5 text-blue-600" />
                {editingCamera ? "Editar C치mara" : "Nueva C치mara"}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4 pt-2">
              <div className="space-y-2">
                <Label>Nombre</Label>
                <Input value={formData.name} onChange={(e) => setFormData({...formData, name: e.target.value})} placeholder="Ej: Pasillo A" required />
              </div>
              <div className="space-y-2">
                <Label>URL RTSP / Fuente</Label>
                <Input value={formData.rtsp_url} onChange={(e) => setFormData({...formData, rtsp_url: e.target.value})} placeholder="0 o rtsp://..." required />
              </div>
              <div className="flex items-center justify-between p-3 bg-slate-50 rounded-lg border">
                <Label>Habilitar detecci칩n</Label>
                <Switch checked={formData.enabled} onCheckedChange={(checked) => setFormData({...formData, enabled: checked})} />
              </div>
              <DialogFooter>
                <Button type="submit" className="w-full">{editingCamera ? "Actualizar" : "Crear"}</Button>
              </DialogFooter>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* STATS CARDS */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="border-0 shadow-sm border-l-4 border-l-emerald-500">
          <CardContent className="pt-4">
            <div className="flex items-center justify-between">
              <div><p className="text-xs font-bold text-slate-500">ACTIVAS</p><p className="text-2xl font-bold">{runningCameras}</p></div>
              <Play className="h-5 w-5 text-emerald-500" />
            </div>
          </CardContent>
        </Card>
        <Card className="border-0 shadow-sm border-l-4 border-l-slate-400">
          <CardContent className="pt-4">
            <div className="flex items-center justify-between">
              <div><p className="text-xs font-bold text-slate-500">DETENIDAS</p><p className="text-2xl font-bold">{stoppedCameras}</p></div>
              <Square className="h-5 w-5 text-slate-400" />
            </div>
          </CardContent>
        </Card>
        <Card className="border-0 shadow-sm border-l-4 border-l-blue-500">
          <CardContent className="pt-4">
            <div className="flex items-center justify-between">
              <div><p className="text-xs font-bold text-slate-500">ACTIVIDAD</p><p className="text-2xl font-bold">{Math.round((runningCameras/cameras.length)*100)}%</p></div>
              <Activity className="h-5 w-5 text-blue-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* TABLA */}
      <Card className="border-0 shadow-sm">
        <CardHeader className="bg-slate-50/50 border-b">
          <div className="flex items-center gap-2">
            <Server className="h-4 w-4 text-slate-500" />
            <CardTitle className="text-sm">Listado de Dispositivos</CardTitle>
          </div>
        </CardHeader>
        <CardContent className="p-0">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>ID</TableHead>
                <TableHead>C치mara</TableHead>
                <TableHead className="text-center">Estado</TableHead>
                <TableHead className="text-right">Acciones</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {pageData.map((camera) => (
                <TableRow key={camera.id}>
                  <TableCell className="font-mono text-xs">{camera.id}</TableCell>
                  <TableCell className="font-semibold">{camera.name}</TableCell>
                  <TableCell className="text-center">
                    <Badge className={camera.status === "RUNNING" ? "bg-emerald-100 text-emerald-700" : "bg-slate-100 text-slate-600"}>
                      {camera.status}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right flex justify-end gap-2">
                    <Button size="sm" onClick={() => { setActiveVideoCamera(camera); setIsVideoModalOpen(true); }} disabled={camera.status === "STOPPED"}>
                      <Eye className="h-4 w-4 mr-1" /> Ver
                    </Button>
                    <Button size="sm" variant="outline" onClick={() => handleStartStop(camera.id, camera.status)}>
                      {camera.status === "RUNNING" ? <Square className="h-4 w-4" /> : <Play className="h-4 w-4" />}
                    </Button>
                    <Button size="sm" variant="outline" onClick={() => handleEdit(camera)}>
                      <Pencil className="h-4 w-4" />
                    </Button>
                    <Button size="sm" variant="outline" className="text-rose-600" onClick={() => handleDelete(camera.id, camera.name)}>
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>

          {/* PAGINACI칍N */}
          <div className="p-4 border-t flex items-center justify-between bg-slate-50/30">
            <span className="text-xs text-slate-500">P치gina {page} de {totalPages}</span>
            <div className="flex gap-2">
              <Button variant="outline" size="sm" onClick={goPrev} disabled={!canPrev}><ChevronLeft className="h-4 w-4" /></Button>
              <Button variant="outline" size="sm" onClick={goNext} disabled={!canNext}><ChevronRight className="h-4 w-4" /></Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* MODAL DE VIDEO */}
      <Dialog open={isVideoModalOpen} onOpenChange={setIsVideoModalOpen}>
        <DialogContent className="sm:max-w-4xl bg-slate-950 border-slate-800">
          <DialogHeader><DialogTitle className="text-slate-100 uppercase text-xs tracking-widest">En Vivo: {activeVideoCamera?.name}</DialogTitle></DialogHeader>
          <div className="aspect-video bg-black rounded-lg overflow-hidden border border-slate-800">
            {activeVideoCamera && (
              <img src={`http://localhost:8000/api/v1/inference/stream/${activeVideoCamera.id}`} alt="Live" className="w-full h-full object-contain" />
            )}
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}
