import { LayoutDashboard, Camera, AlertTriangle, Image, Bell, Settings, Shield } from 'lucide-react';
import {
  Sidebar,
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarHeader,
  SidebarFooter,
} from './ui/sidebar';
import { Badge } from './ui/badge';

interface AdminSidebarProps {
  activeModule: string;
  onModuleChange: (module: string) => void;
}

const menuItems = [
  {
    title: 'Dashboard',
    icon: LayoutDashboard,
    id: 'dashboard',
    badge: null,
    description: 'Panel principal'
  },
  {
    title: 'Cámaras',
    icon: Camera,
    id: 'cameras',
    badge: '24',
    description: 'Gestión de dispositivos'
  },
  {
    title: 'Incidentes',
    icon: AlertTriangle,
    id: 'incidents',
    badge: '7',
    description: 'Detecciones activas'
  },
  {
    title: 'Evidencias',
    icon: Image,
    id: 'evidence',
    badge: null,
    description: 'Galería de capturas'
  },
  {
    title: 'Alertas',
    icon: Bell,
    id: 'alerts',
    badge: '3',
    description: 'Notificaciones'
  },
  {
    title: 'Configuración',
    icon: Settings,
    id: 'settings',
    badge: null,
    description: 'Parámetros del sistema'
  },
];

export function AdminSidebar({ activeModule, onModuleChange }: AdminSidebarProps) {
  return (
    <Sidebar 
      variant="sidebar" 
      collapsible="icon" 
      className="border-r border-border bg-card shadow-sm transition-all duration-300"
    >
      {/* Header del Sidebar - Se oculta el texto cuando está colapsado */}
      <SidebarHeader className="border-b p-4 bg-primary shrink-0 transition-colors">
        <div className="flex items-center gap-3">
          <div className="bg-white/20 backdrop-blur-sm p-2 rounded-lg shadow-sm ring-1 ring-white/30">
            <Shield className="h-5 w-5 text-white" />
          </div>
          <div className="flex flex-col group-data-[collapsible=icon]:hidden overflow-hidden transition-all">
            <h2 className="font-bold text-sm text-white whitespace-nowrap">Sentinel AI</h2>
            <p className="text-[10px] text-white/70 font-medium uppercase tracking-tighter">Vigilancia Inteligente</p>
          </div>
        </div>
      </SidebarHeader>
      
      {/* Contenido principal - Navegación */}
      <SidebarContent className="flex-1 py-4 overflow-x-hidden">
        <SidebarGroup>
          <SidebarGroupLabel className="px-4 text-[10px] uppercase tracking-widest text-muted-foreground font-bold mb-2 group-data-[collapsible=icon]:hidden">
            Módulos del Sistema
          </SidebarGroupLabel>
          <SidebarGroupContent>
            <SidebarMenu className="gap-1 px-2">
              {menuItems.map((item) => (
                <SidebarMenuItem key={item.id}>
                  <SidebarMenuButton
                    onClick={() => onModuleChange(item.id)}
                    isActive={activeModule === item.id}
                    tooltip={item.title}
                    className={`
                      w-full rounded-lg transition-all duration-200 h-auto py-2.5 px-3
                      ${activeModule === item.id 
                        ? 'bg-primary text-primary-foreground shadow-md hover:bg-primary/90' 
                        : 'text-muted-foreground hover:bg-muted hover:text-foreground'
                      }
                    `}
                  >
                    <item.icon className={`h-4 w-4 shrink-0 ${activeModule === item.id ? 'text-white' : ''}`} />
                    
                    <div className="flex-1 text-left group-data-[collapsible=icon]:hidden overflow-hidden ml-3">
                      <span className="font-semibold text-xs block">{item.title}</span>
                      <p className={`text-[10px] leading-tight truncate ${
                        activeModule === item.id ? 'text-white/70' : 'text-muted-foreground'
                      }`}>
                        {item.description}
                      </p>
                    </div>

                    {item.badge && (
                      <Badge 
                        className={`ml-auto font-bold text-[9px] px-1.5 py-0 group-data-[collapsible=icon]:hidden ${
                          activeModule === item.id 
                            ? 'bg-white/20 text-white' 
                            : 'bg-destructive/10 text-destructive border-0'
                        }`}
                      >
                        {item.badge}
                      </Badge>
                    )}
                  </SidebarMenuButton>
                </SidebarMenuItem>
              ))}
            </SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      </SidebarContent>

      {/* Footer - Estado del sistema */}
      <SidebarFooter className="border-t p-4 bg-muted/20 shrink-0">
        <div className="flex flex-col gap-2 group-data-[collapsible=icon]:items-center">
          <div className="flex items-center gap-2 p-2 bg-background rounded-lg border border-border shadow-sm group-data-[collapsible=icon]:p-2">
            <div className="h-2 w-2 rounded-full bg-emerald-500 animate-pulse shadow-[0_0_8px_rgba(16,185,129,0.5)] shrink-0" />
            <p className="text-[10px] text-foreground font-bold uppercase tracking-wider group-data-[collapsible=icon]:hidden">
              En Línea
            </p>
          </div>
          <div className="text-center group-data-[collapsible=icon]:hidden">
            <p className="text-[10px] text-muted-foreground font-medium">© 2026 Sentinel AI</p>
            <p className="text-[10px] text-muted-foreground/60">v2.1.0</p>
          </div>
        </div>
      </SidebarFooter>
    </Sidebar>
  );
}
