import { http } from "./http";

export type Alert = {
  id: string | number;
  camera_id?: string | number;
  weapon_type?: string;
  type?: string;
  confidence?: number;     // puede venir 0-1 o 0-100
  severity?: string;
  created_at?: string;     // ISO
  timestamp?: string;      // si tu backend lo manda así
  camera_code?: string;    // si lo manda ya resuelto
  location?: string;
};

export async function listAlerts() {
  const { data } = await http.get<Alert[]>("/alerts");
  return data;
}
