import React, { useMemo, useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Switch } from "./ui/switch";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "./ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "./ui/table";
import {
  Play,
  Square,
  Pencil,
  Trash2,
  Plus,
  Video,
  Activity,
  Server,
  ChevronLeft,
  ChevronRight,
} from "lucide-react";
import { toast } from "sonner";

interface Camera {
  id: string;
  name: string;
  rtsp_url: string;
  enabled: boolean;
  fps_target: number;
  infer_every_n_frames: number;
  status: "RUNNING" | "STOPPED";
}

const initialCameras: Camera[] = [
  {
    id: "CAM-001",
    name: "Entrada Principal",
    rtsp_url: "rtsp://192.168.1.100:554/stream1",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "RUNNING",
  },
  {
    id: "CAM-002",
    name: "Estacionamiento Norte",
    rtsp_url: "rtsp://192.168.1.101:554/stream1",
    enabled: true,
    fps_target: 25,
    infer_every_n_frames: 10,
    status: "RUNNING",
  },
  {
    id: "CAM-003",
    name: "Recepci√≥n Principal",
    rtsp_url: "rtsp://192.168.1.102:554/stream1",
    enabled: false,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "STOPPED",
  },
  {
    id: "CAM-004",
    name: "Pasillo Norte - Piso 2",
    rtsp_url: "rtsp://192.168.1.103:554/stream1",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "RUNNING",
  },
  {
    id: "CAM-005",
    name: "Salida de Emergencia",
    rtsp_url: "rtsp://192.168.1.104:554/stream1",
    enabled: false,
    fps_target: 20,
    infer_every_n_frames: 8,
    status: "STOPPED",
  },
  {
    id: "CAM-006",
    name: "Cafeter√≠a",
    rtsp_url: "rtsp://192.168.1.105:554/stream1",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "RUNNING",
  },
];

export function Cameras() {
  const [cameras, setCameras] = useState<Camera[]>(initialCameras);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingCamera, setEditingCamera] = useState<Camera | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    rtsp_url: "",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
  });

  // ‚úÖ PAGINACI√ìN
  const pageSize = 10;
  const [page, setPage] = useState(1);

  const totalPages = Math.max(1, Math.ceil(cameras.length / pageSize));

  // si borras y te quedas fuera de rango, corrige la p√°gina
  useEffect(() => {
    setPage((p) => Math.min(p, totalPages));
  }, [totalPages]);

  const pageData = useMemo(() => {
    const start = (page - 1) * pageSize;
    return cameras.slice(start, start + pageSize);
  }, [cameras, page]);

  const canPrev = page > 1;
  const canNext = page < totalPages;

  const goPrev = () => setPage((p) => Math.max(1, p - 1));
  const goNext = () => setPage((p) => Math.min(totalPages, p + 1));

  const handleStartStop = (id: string, currentStatus: string) => {
    setCameras((prev) =>
      prev.map((cam) =>
        cam.id === id
          ? {
              ...cam,
              status:
                currentStatus === "RUNNING"
                  ? "STOPPED"
                  : ("RUNNING" as "RUNNING" | "STOPPED"),
            }
          : cam
      )
    );

    toast.success(
      currentStatus === "RUNNING"
        ? "üî¥ C√°mara detenida exitosamente"
        : "üü¢ C√°mara iniciada exitosamente"
    );
  };

  const handleDelete = (id: string, name: string) => {
    setCameras((prev) => prev.filter((cam) => cam.id !== id));
    toast.success(`üóëÔ∏è C√°mara "${name}" eliminada exitosamente`);
  };

  const handleEdit = (camera: Camera) => {
    setEditingCamera(camera);
    setFormData({
      name: camera.name,
      rtsp_url: camera.rtsp_url,
      enabled: camera.enabled,
      fps_target: camera.fps_target,
      infer_every_n_frames: camera.infer_every_n_frames,
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (editingCamera) {
      setCameras((prev) =>
        prev.map((cam) =>
          cam.id === editingCamera.id ? { ...cam, ...formData } : cam
        )
      );
      toast.success(`‚úÖ C√°mara "${formData.name}" actualizada exitosamente`);
    } else {
      const newCamera: Camera = {
        id: `CAM-${String(cameras.length + 1).padStart(3, "0")}`,
        ...formData,
        status: formData.enabled ? "RUNNING" : "STOPPED",
      };
      setCameras((prev) => [...prev, newCamera]);
      toast.success(`‚úÖ C√°mara "${formData.name}" creada exitosamente`);
    }

    setIsDialogOpen(false);
    setEditingCamera(null);
    setFormData({
      name: "",
      rtsp_url: "",
      enabled: true,
      fps_target: 30,
      infer_every_n_frames: 5,
    });
  };

  const openCreateDialog = () => {
    setEditingCamera(null);
    setFormData({
      name: "",
      rtsp_url: "",
      enabled: true,
      fps_target: 30,
      infer_every_n_frames: 5,
    });
    setIsDialogOpen(true);
  };

  const runningCameras = cameras.filter((c) => c.status === "RUNNING").length;
  const stoppedCameras = cameras.filter((c) => c.status === "STOPPED").length;

  return (
    <div className="space-y-5">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-slate-900">Gesti√≥n de C√°maras</h1>
          <p className="text-sm text-slate-600 mt-1">
            Administra las c√°maras IP conectadas al sistema de detecci√≥n
          </p>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button
              onClick={openCreateDialog}
              className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 shadow-md hover:shadow-lg transition-all"
              size="lg"
            >
              <Plus className="h-5 w-5 mr-2" />
              Nueva C√°mara
            </Button>
          </DialogTrigger>

          <DialogContent className="sm:max-w-[600px]">
            <DialogHeader>
              <DialogTitle className="text-2xl flex items-center gap-2">
                <Video className="h-6 w-6 text-blue-600" />
                {editingCamera ? "Editar C√°mara" : "Crear Nueva C√°mara"}
              </DialogTitle>
            </DialogHeader>

            <form onSubmit={handleSubmit} className="space-y-5 pt-4">
              <div className="space-y-2">
                <Label htmlFor="name" className="text-slate-700 font-semibold">
                  Nombre de la C√°mara
                </Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  placeholder="Ej: Entrada Principal"
                  className="bg-slate-50 border-slate-300 h-11"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="rtsp_url" className="text-slate-700 font-semibold">
                  URL RTSP
                </Label>
                <Input
                  id="rtsp_url"
                  value={formData.rtsp_url}
                  onChange={(e) => setFormData({ ...formData, rtsp_url: e.target.value })}
                  placeholder="rtsp://192.168.1.100:554/stream1"
                  className="bg-slate-50 border-slate-300 font-mono text-sm h-11"
                  required
                />
                <p className="text-xs text-slate-500">
                  Ingresa la URL del stream RTSP de la c√°mara IP
                </p>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="fps_target" className="text-slate-700 font-semibold">
                    FPS Target
                  </Label>
                  <Input
                    id="fps_target"
                    type="number"
                    min="1"
                    max="60"
                    value={formData.fps_target}
                    onChange={(e) =>
                      setFormData({ ...formData, fps_target: parseInt(e.target.value, 10) })
                    }
                    className="bg-slate-50 border-slate-300 h-11"
                    required
                  />
                  <p className="text-xs text-slate-500">Cuadros por segundo (1-60)</p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="infer_every_n_frames" className="text-slate-700 font-semibold">
                    Inferir cada N frames
                  </Label>
                  <Input
                    id="infer_every_n_frames"
                    type="number"
                    min="1"
                    max="30"
                    value={formData.infer_every_n_frames}
                    onChange={(e) =>
                      setFormData({
                        ...formData,
                        infer_every_n_frames: parseInt(e.target.value, 10),
                      })
                    }
                    className="bg-slate-50 border-slate-300 h-11"
                    required
                  />
                  <p className="text-xs text-slate-500">Frecuencia de an√°lisis (1-30)</p>
                </div>
              </div>

              <div className="flex items-center justify-between p-5 bg-blue-50 rounded-xl border-2 border-blue-200">
                <div className="space-y-0.5">
                  <Label htmlFor="enabled" className="text-slate-900 font-semibold cursor-pointer">
                    Iniciar Autom√°ticamente
                  </Label>
                  <p className="text-sm text-slate-600">
                    La c√°mara comenzar√° a detectar al crearla
                  </p>
                </div>
                <Switch
                  id="enabled"
                  checked={formData.enabled}
                  onCheckedChange={(checked) => setFormData({ ...formData, enabled: checked })}
                />
              </div>

              <DialogFooter className="gap-2 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsDialogOpen(false)}
                  className="h-11"
                >
                  Cancelar
                </Button>
                <Button
                  type="submit"
                  className="bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 h-11 px-8"
                >
                  {editingCamera ? "üíæ Actualizar" : "‚ûï Crear"} C√°mara
                </Button>
              </DialogFooter>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* ‚úÖ Stats Cards (tama√±o reducido) */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="border-0 shadow-sm">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">Total de C√°maras</p>
                <p className="text-2xl font-bold text-slate-900 mt-1">{cameras.length}</p>
              </div>
              <div className="bg-blue-100 p-2.5 rounded-xl">
                <Video className="h-5 w-5 text-blue-600" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="border-0 shadow-sm border-l-4 border-l-emerald-500">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">C√°maras Activas</p>
                <p className="text-2xl font-bold text-emerald-600 mt-1">{runningCameras}</p>
              </div>
              <div className="bg-emerald-100 p-2.5 rounded-xl">
                <Play className="h-5 w-5 text-emerald-600" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="border-0 shadow-sm border-l-4 border-l-slate-400">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">C√°maras Detenidas</p>
                <p className="text-2xl font-bold text-slate-600 mt-1">{stoppedCameras}</p>
              </div>
              <div className="bg-slate-100 p-2.5 rounded-xl">
                <Square className="h-5 w-5 text-slate-600" />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card className="border-0 shadow-sm border-l-4 border-l-blue-500">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">Tasa de Actividad</p>
                <p className="text-2xl font-bold text-blue-600 mt-1">
                  {cameras.length > 0 ? Math.round((runningCameras / cameras.length) * 100) : 0}%
                </p>
              </div>
              <div className="bg-blue-100 p-2.5 rounded-xl">
                <Activity className="h-5 w-5 text-blue-600" />
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Tabla de c√°maras */}
<Card className="border border-slate-200/70 shadow-sm overflow-hidden">
  <CardHeader className="border-b bg-slate-50/70 px-6 py-4">
    <div className="flex items-center justify-between">
      <div className="flex items-center gap-3">
        <div className="bg-slate-200/70 p-2 rounded-lg">
          <Server className="h-5 w-5 text-slate-700" />
        </div>
        <div>
          <CardTitle className="text-lg font-bold text-slate-900">
            C√°maras Registradas
          </CardTitle>
          <p className="text-xs text-slate-600 mt-0.5">
            {cameras.length} c√°maras configuradas en el sistema
          </p>
        </div>
      </div>
    </div>
  </CardHeader>

  <CardContent className="p-0">
    <div className="overflow-x-auto">
      <Table className="min-w-[980px]">
        {/* Header sticky */}
        <TableHeader className="sticky top-0 z-10 bg-white">
          <TableRow className="bg-white hover:bg-white border-b border-slate-200">
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4">
              ID C√°mara
            </TableHead>
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4">
              Nombre
            </TableHead>
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4">
              URL RTSP
            </TableHead>
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4 text-center">
              FPS
            </TableHead>
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4 text-center">
              Inferencia
            </TableHead>
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4 text-center">
              Estado
            </TableHead>
            <TableHead className="text-[11px] font-bold tracking-wide text-slate-600 uppercase px-6 py-4 text-right">
              Acciones
            </TableHead>
          </TableRow>
        </TableHeader>

        <TableBody>
          {cameras.map((camera) => (
            <TableRow
              key={camera.id}
              className="border-b border-slate-100 hover:bg-slate-50/60 transition-colors"
            >
              {/* ID */}
              <TableCell className="px-6 py-4">
                <span className="font-mono text-xs font-bold text-slate-900">
                  {camera.id}
                </span>
              </TableCell>

              {/* Nombre */}
              <TableCell className="px-6 py-4">
                <div className="flex items-center gap-3">
                  <div
                    className={`h-9 w-9 rounded-xl flex items-center justify-center border ${
                      camera.status === "RUNNING"
                        ? "bg-emerald-50 border-emerald-200"
                        : "bg-slate-50 border-slate-200"
                    }`}
                  >
                    <Video
                      className={`h-4 w-4 ${
                        camera.status === "RUNNING"
                          ? "text-emerald-600"
                          : "text-slate-500"
                      }`}
                    />
                  </div>
                  <div className="min-w-0">
                    <p className="text-sm font-semibold text-slate-900 truncate max-w-[220px]">
                      {camera.name}
                    </p>
                    <p className="text-[11px] text-slate-500">
                      {camera.enabled ? "Auto inicio: S√≠" : "Auto inicio: No"}
                    </p>
                  </div>
                </div>
              </TableCell>

              {/* RTSP */}
              <TableCell className="px-6 py-4">
                <div className="max-w-[420px]">
                  <div className="truncate rounded-lg border border-slate-200 bg-slate-50 px-3 py-2 font-mono text-[12px] text-slate-700">
                    {camera.rtsp_url}
                  </div>
                </div>
              </TableCell>

              {/* FPS */}
              <TableCell className="px-6 py-4 text-center">
                <Badge
                  variant="outline"
                  className="bg-blue-50 text-blue-700 border-blue-200 font-bold font-mono text-xs px-3 py-1"
                >
                  {camera.fps_target} fps
                </Badge>
              </TableCell>

              {/* Inferencia */}
              <TableCell className="px-6 py-4 text-center">
                <Badge
                  variant="outline"
                  className="bg-purple-50 text-purple-700 border-purple-200 font-bold font-mono text-xs px-3 py-1"
                >
                  1/{camera.infer_every_n_frames}
                </Badge>
              </TableCell>

              {/* Estado */}
              <TableCell className="px-6 py-4 text-center">
                <Badge
                  variant="outline"
                  className={`inline-flex items-center gap-2 font-bold text-xs px-4 py-1.5 rounded-full ${
                    camera.status === "RUNNING"
                      ? "bg-emerald-50 text-emerald-700 border-emerald-200"
                      : "bg-slate-50 text-slate-600 border-slate-200"
                  }`}
                >
                  <span
                    className={`h-2 w-2 rounded-full ${
                      camera.status === "RUNNING"
                        ? "bg-emerald-500 animate-pulse"
                        : "bg-slate-400"
                    }`}
                  />
                  {camera.status}
                </Badge>
              </TableCell>

              {/* Acciones */}
              <TableCell className="px-6 py-4">
                <div className="flex items-center justify-end gap-2">
                  {camera.status === "STOPPED" ? (
                    <Button
                      size="sm"
                      onClick={() => handleStartStop(camera.id, camera.status)}
                      className="h-9 px-4 bg-emerald-600 hover:bg-emerald-700 text-white shadow-sm"
                    >
                      <Play className="h-4 w-4 mr-1.5" />
                      Iniciar
                    </Button>
                  ) : (
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleStartStop(camera.id, camera.status)}
                      className="h-9 px-4 border-rose-300 text-rose-700 hover:bg-rose-50 hover:border-rose-400"
                    >
                      <Square className="h-4 w-4 mr-1.5" />
                      Detener
                    </Button>
                  )}

                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleEdit(camera)}
                    className="h-9 w-9 p-0 hover:bg-blue-50 hover:text-blue-700 hover:border-blue-300"
                    aria-label="Editar"
                    title="Editar"
                  >
                    <Pencil className="h-4 w-4" />
                  </Button>

                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => handleDelete(camera.id, camera.name)}
                    className="h-9 w-9 p-0 hover:bg-rose-50 hover:text-rose-700 hover:border-rose-300"
                    aria-label="Eliminar"
                    title="Eliminar"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  </CardContent>
</Card>


      {/* ‚úÖ ELIMINADO: Info adicional (Configuraci√≥n Recomendada / Estado del Sistema) */}
    </div>
  );
}
