import { http } from "./http";

export type Camera = {
  id: string | number;
  name?: string;
  code?: string;     // por si tu backend usa CAM-001
  status?: string;   // "active"/"inactive" o lo que uses
  is_active?: boolean;
};

export async function listCameras() {
  const { data } = await http.get<Camera[]>("/cameras");
  return data;
}
