import React, { useState, useMemo } from 'react';
import { 
  Eye, 
  Filter, 
  AlertTriangle, 
  Download, 
  Camera, 
  Calendar, 
  TrendingUp, 
  Shield,
  X,
  ChevronDown,
  Check
} from 'lucide-react';

// --- Interfaces ---
interface Incident {
  id: string;
  weapon_type: string;
  confidence: number;
  timestamp: string;
  date: string;
  time: string;
  camera_id: string;
  camera_name: string;
  image_url: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
}

interface SeverityConfig {
  bg: string;
  text: string;
  border: string;
  dot: string;
  icon: string;
  label: string;
}

// --- Mock Data ---
const mockIncidents: Incident[] = [
  { id: 'INC-001', weapon_type: 'Pistola', confidence: 0.94, timestamp: '2026-02-09 14:32:15', date: '09/02/2026', time: '14:32:15', camera_id: 'CAM-001', camera_name: 'Entrada Principal', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'high' },
  { id: 'INC-002', weapon_type: 'Rifle', confidence: 0.87, timestamp: '2026-02-09 13:18:42', date: '09/02/2026', time: '13:18:42', camera_id: 'CAM-005', camera_name: 'Estacionamiento Norte', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'critical' },
  { id: 'INC-003', weapon_type: 'Cuchillo', confidence: 0.76, timestamp: '2026-02-09 12:05:28', date: '09/02/2026', time: '12:05:28', camera_id: 'CAM-012', camera_name: 'Pasillo Norte - Piso 2', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'medium' },
  { id: 'INC-004', weapon_type: 'Pistola', confidence: 0.91, timestamp: '2026-02-09 11:44:11', date: '09/02/2026', time: '11:44:11', camera_id: 'CAM-003', camera_name: 'RecepciÃ³n Principal', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'high' },
  { id: 'INC-005', weapon_type: 'Objeto Sospechoso', confidence: 0.68, timestamp: '2026-02-09 10:22:33', date: '09/02/2026', time: '10:22:33', camera_id: 'CAM-008', camera_name: 'Salida de Emergencia', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'low' },
  { id: 'INC-006', weapon_type: 'Pistola', confidence: 0.82, timestamp: '2026-02-09 09:15:47', date: '09/02/2026', time: '09:15:47', camera_id: 'CAM-001', camera_name: 'Entrada Principal', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'high' },
  { id: 'INC-007', weapon_type: 'Rifle', confidence: 0.95, timestamp: '2026-02-09 08:45:22', date: '09/02/2026', time: '08:45:22', camera_id: 'CAM-005', camera_name: 'Estacionamiento Norte', image_url: 'https://images.unsplash.com/photo-1551836022-d5d88e9218df?w=800', severity: 'critical' },
  { id: 'INC-008', weapon_type: 'Cuchillo', confidence: 0.72, timestamp: '2026-02-09 07:33:10', date: '09/02/2026', time: '07:33:10', camera_id: 'CAM-006', camera_name: 'CafeterÃ­a', image_url: 'https://images.unsplash.com/photo-1568605114967-8130f3a36994?w=800', severity: 'medium' },
  { id: 'INC-009', weapon_type: 'Pistola', confidence: 0.88, timestamp: '2026-02-09 06:12:55', date: '09/02/2026', time: '06:12:55', camera_id: 'CAM-003', camera_name: 'RecepciÃ³n Principal', image_url: 'https://images.unsplash.com/photo-1486312338219-ce68d2c6f44d?w=800', severity: 'high' },
  { id: 'INC-010', weapon_type: 'Objeto Sospechoso', confidence: 0.65, timestamp: '2026-02-09 05:28:40', date: '09/02/2026', time: '05:28:40', camera_id: 'CAM-012', camera_name: 'Pasillo Norte - Piso 2', image_url: 'https://images.unsplash.com/photo-1518770660439-4636190af475?w=800', severity: 'low' },
];

// --- Simple UI Components ---
const Card = ({ children, className = "" }: { children: React.ReactNode; className?: string }) => (
  <div className={`bg-white rounded-xl shadow-sm overflow-hidden ${className}`}>{children}</div>
);

const Badge = ({ children, className = "" }: { children: React.ReactNode; className?: string }) => (
  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-semibold ${className}`}>{children}</span>
);

const Button = ({ 
  children, 
  onClick, 
  variant = "primary", 
  className = "", 
  size = "md" 
}: { 
  children: React.ReactNode; 
  onClick?: () => void; 
  variant?: "primary" | "outline" | "ghost"; 
  className?: string; 
  size?: "sm" | "md" | "lg" 
}) => {
  const variants = {
    primary: "bg-blue-600 text-white hover:bg-blue-700 shadow-sm",
    outline: "border border-slate-200 bg-white text-slate-700 hover:bg-slate-50",
    ghost: "text-slate-600 hover:bg-slate-100",
  };
  const sizes = { sm: "px-3 py-1.5 text-sm", md: "px-4 py-2", lg: "px-6 py-3 text-lg" };
  
  return (
    <button 
      onClick={onClick} 
      className={`inline-flex items-center justify-center rounded-lg font-medium transition-colors gap-2 ${variants[variant]} ${sizes[size]} ${className}`}
    >
      {children}
    </button>
  );
};

// --- Main Application Component ---
export const Incidents = () => {
  const [selectedIncident, setSelectedIncident] = useState<Incident | null>(null);
  const [isDetailOpen, setIsDetailOpen] = useState(false);
  const [filters, setFilters] = useState({
    dateFrom: '',
    dateTo: '',
    weaponType: 'all',
    minConfidence: '',
    camera: 'all',
    severity: 'all'
  });

  const weaponTypes = useMemo(() => Array.from(new Set(mockIncidents.map(i => i.weapon_type))), []);

  const cameras = useMemo(() => {
    const uniqueMap = new Map<string, { id: string; name: string }>();
    mockIncidents.forEach(incident => {
      if (!uniqueMap.has(incident.camera_id)) {
        uniqueMap.set(incident.camera_id, { id: incident.camera_id, name: incident.camera_name });
      }
    });
    return Array.from(uniqueMap.values());
  }, []);

  const filteredIncidents = useMemo(() => {
    return mockIncidents.filter(incident => {
      if (filters.weaponType !== 'all' && incident.weapon_type !== filters.weaponType) return false;
      if (filters.camera !== 'all' && incident.camera_id !== filters.camera) return false;
      if (filters.severity !== 'all' && incident.severity !== filters.severity) return false;
      if (filters.minConfidence && incident.confidence < parseFloat(filters.minConfidence)) return false;
      return true;
    });
  }, [filters]);

  const stats = useMemo(() => {
    const counts: Record<string, number> = {
      total: filteredIncidents.length,
      critical: filteredIncidents.filter(i => i.severity === 'critical').length,
      high: filteredIncidents.filter(i => i.severity === 'high').length,
      medium: filteredIncidents.filter(i => i.severity === 'medium').length,
      low: filteredIncidents.filter(i => i.severity === 'low').length,
    };
    return counts;
  }, [filteredIncidents]);

  const getSeverityConfig = (severity: string): SeverityConfig => {
    const config: Record<string, SeverityConfig> = {
      critical: { bg: 'bg-rose-100', text: 'text-rose-700', border: 'border-rose-300', dot: 'bg-rose-500', icon: 'ðŸ”´', label: 'CrÃ­tica' },
      high: { bg: 'bg-orange-100', text: 'text-orange-700', border: 'border-orange-300', dot: 'bg-orange-500', icon: 'ðŸŸ ', label: 'Alta' },
      medium: { bg: 'bg-amber-100', text: 'text-amber-700', border: 'border-amber-300', dot: 'bg-amber-500', icon: 'ðŸŸ¡', label: 'Media' },
      low: { bg: 'bg-slate-100', text: 'text-slate-700', border: 'border-slate-300', dot: 'bg-slate-400', icon: 'âšª', label: 'Baja' },
    };
    return config[severity] || config.low;
  };

  return (
    <div className="p-8 bg-slate-50 min-h-screen space-y-8">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-slate-900 tracking-tight">Incidentes Detectados</h1>
          <p className="text-slate-500 mt-1">Historial completo de detecciones de seguridad por IA</p>
        </div>
        <Button variant="outline" size="lg" className="hover:border-blue-400">
          <Download className="h-5 w-5" /> Exportar Reporte
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
        <Card className="p-6 border-l-4 border-l-blue-500">
          <p className="text-sm font-medium text-slate-500 uppercase tracking-wider">Total</p>
          <div className="flex items-end justify-between mt-2">
            <span className="text-3xl font-bold text-slate-900">{stats.total}</span>
            <AlertTriangle className="h-8 w-8 text-blue-100" />
          </div>
        </Card>
        {['critical', 'high', 'medium', 'low'].map(sev => {
          const cfg = getSeverityConfig(sev);
          return (
            <Card key={sev} className={`p-6 border-l-4 ${sev === 'critical' ? 'border-l-rose-500' : sev === 'high' ? 'border-l-orange-500' : sev === 'medium' ? 'border-l-amber-500' : 'border-l-slate-400'}`}>
              <p className="text-sm font-medium text-slate-500 uppercase tracking-wider">{cfg.label}</p>
              <div className="flex items-end justify-between mt-2">
                <span className={`text-3xl font-bold ${cfg.text}`}>{stats[sev]}</span>
                <span className="text-2xl opacity-40">{cfg.icon}</span>
              </div>
            </Card>
          );
        })}
      </div>

      {/* Filters Section */}
      <Card>
        <div className="bg-slate-50 border-b p-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <Filter className="h-5 w-5 text-blue-600" />
            <span className="font-bold text-slate-900 uppercase text-sm tracking-widest">Panel de Filtrado</span>
          </div>
          <button 
            onClick={() => setFilters({ dateFrom: '', dateTo: '', weaponType: 'all', minConfidence: '', camera: 'all', severity: 'all' })} 
            className="text-xs text-blue-600 hover:underline"
          >
            Limpiar Filtros
          </button>
        </div>
        <div className="p-6 grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
          <div className="space-y-1.5">
            <label className="text-xs font-bold text-slate-500 flex items-center gap-1"><Calendar size={12}/> FECHA DESDE</label>
            <input type="date" value={filters.dateFrom} onChange={e => setFilters({...filters, dateFrom: e.target.value})} className="w-full border rounded-lg px-3 py-2 text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none" />
          </div>
          <div className="space-y-1.5">
            <label className="text-xs font-bold text-slate-500 flex items-center gap-1"><Shield size={12}/> TIPO ARMA</label>
            <select value={filters.weaponType} onChange={e => setFilters({...filters, weaponType: e.target.value})} className="w-full border rounded-lg px-3 py-2 text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none">
              <option value="all">Todos</option>
              {weaponTypes.map(type => <option key={type} value={type}>{type}</option>)}
            </select>
          </div>
          <div className="space-y-1.5">
            <label className="text-xs font-bold text-slate-500 flex items-center gap-1"><Camera size={12}/> CÃMARA</label>
            <select value={filters.camera} onChange={e => setFilters({...filters, camera: e.target.value})} className="w-full border rounded-lg px-3 py-2 text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none">
              <option value="all">Todas</option>
              {cameras.map(cam => <option key={cam.id} value={cam.id}>{cam.id} - {cam.name}</option>)}
            </select>
          </div>
          <div className="space-y-1.5">
            <label className="text-xs font-bold text-slate-500 flex items-center gap-1"><TrendingUp size={12}/> CONFIANZA MIN</label>
            <input type="number" step="0.01" value={filters.minConfidence} onChange={e => setFilters({...filters, minConfidence: e.target.value})} placeholder="0.0 - 1.0" className="w-full border rounded-lg px-3 py-2 text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none" />
          </div>
          <div className="space-y-1.5">
            <label className="text-xs font-bold text-slate-500 flex items-center gap-1"><AlertTriangle size={12}/> SEVERIDAD</label>
            <select value={filters.severity} onChange={e => setFilters({...filters, severity: e.target.value})} className="w-full border rounded-lg px-3 py-2 text-sm bg-slate-50 focus:ring-2 focus:ring-blue-500 outline-none">
              <option value="all">Todas</option>
              <option value="critical">CrÃ­tica</option>
              <option value="high">Alta</option>
              <option value="medium">Media</option>
              <option value="low">Baja</option>
            </select>
          </div>
        </div>
      </Card>

      {/* Results Table */}
      <Card>
        <div className="overflow-x-auto">
          <table className="w-full text-left border-collapse">
            <thead>
              <tr className="bg-slate-50 border-b text-slate-500 text-xs font-bold uppercase tracking-widest">
                <th className="px-6 py-4">ID Incidente</th>
                <th className="px-6 py-4">Tipo de Arma</th>
                <th className="px-6 py-4 text-center">Confianza</th>
                <th className="px-6 py-4">Fecha / Hora</th>
                <th className="px-6 py-4">CÃ¡mara</th>
                <th className="px-6 py-4 text-center">Severidad</th>
                <th className="px-6 py-4 text-right">AcciÃ³n</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-100">
              {filteredIncidents.map(inc => {
                const cfg = getSeverityConfig(inc.severity);
                return (
                  <tr key={inc.id} className="hover:bg-slate-50/80 transition-colors group">
                    <td className="px-6 py-4 font-mono font-bold text-slate-900">{inc.id}</td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <div className={`h-2.5 w-2.5 rounded-full ${cfg.dot} ${inc.severity === 'critical' ? 'animate-pulse' : ''}`} />
                        <span className="font-semibold">{inc.weapon_type}</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-3">
                        <div className="flex-1 min-w-16 bg-slate-100 rounded-full h-1.5 overflow-hidden">
                          <div 
                            className={`h-full ${inc.confidence > 0.9 ? 'bg-emerald-500' : inc.confidence > 0.7 ? 'bg-amber-500' : 'bg-rose-500'}`} 
                            style={{ width: `${inc.confidence * 100}%` }}
                          />
                        </div>
                        <span className="font-mono text-xs font-bold">{(inc.confidence * 100).toFixed(0)}%</span>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-sm">
                        <div className="font-bold">{inc.time}</div>
                        <div className="text-slate-400 text-xs">{inc.date}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-sm">
                        <div className="font-bold">{inc.camera_id}</div>
                        <div className="text-slate-400 text-xs truncate max-w-[120px]">{inc.camera_name}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 text-center">
                      <Badge className={`${cfg.bg} ${cfg.text} border ${cfg.border} shadow-sm px-3`}>{cfg.label}</Badge>
                    </td>
                    <td className="px-6 py-4 text-right">
                      <Button onClick={() => { setSelectedIncident(inc); setIsDetailOpen(true); }} size="sm" className="opacity-0 group-hover:opacity-100 transition-opacity">
                        <Eye size={14} /> Detalle
                      </Button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          {filteredIncidents.length === 0 && (
            <div className="p-12 text-center text-slate-400">No se encontraron incidentes con los filtros actuales</div>
          )}
        </div>
      </Card>

      {/* Detail Modal */}
      {isDetailOpen && selectedIncident && (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-slate-900/60 backdrop-blur-sm">
          <div className="bg-white rounded-2xl shadow-2xl w-full max-w-4xl max-h-[90vh] overflow-y-auto relative animate-in fade-in zoom-in duration-200">
            <button onClick={() => setIsDetailOpen(false)} className="absolute top-4 right-4 p-2 hover:bg-slate-100 rounded-full transition-colors text-slate-400 hover:text-slate-900 z-10">
              <X size={24} />
            </button>
            
            <div className="p-8 space-y-8">
              <div className="flex items-center gap-3">
                <div className="p-2 bg-blue-100 rounded-lg"><Shield className="text-blue-600 h-6 w-6" /></div>
                <h2 className="text-2xl font-bold">Evidencia del Incidente {selectedIncident.id}</h2>
              </div>

              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div className="p-4 bg-slate-50 rounded-xl border">
                  <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Arma</p>
                  <p className="text-lg font-bold text-slate-900 mt-1">{selectedIncident.weapon_type}</p>
                </div>
                <div className="p-4 bg-slate-50 rounded-xl border">
                  <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Confianza</p>
                  <p className="text-lg font-bold text-slate-900 mt-1">{(selectedIncident.confidence * 100).toFixed(2)}%</p>
                </div>
                <div className="p-4 bg-slate-50 rounded-xl border">
                  <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">Severidad</p>
                  <Badge className={`${getSeverityConfig(selectedIncident.severity).bg} ${getSeverityConfig(selectedIncident.severity).text} mt-1`}>
                    {getSeverityConfig(selectedIncident.severity).label}
                  </Badge>
                </div>
                <div className="p-4 bg-slate-50 rounded-xl border">
                  <p className="text-[10px] font-bold text-slate-400 uppercase tracking-widest">CÃ¡mara</p>
                  <p className="text-lg font-bold text-slate-900 mt-1">{selectedIncident.camera_id}</p>
                </div>
              </div>

              <div className="space-y-3">
                <h3 className="font-bold text-slate-900 flex items-center gap-2">Captura de CÃ¡mara <Camera size={16}/></h3>
                <div className="rounded-2xl border-4 border-white shadow-2xl overflow-hidden bg-slate-900 relative group">
                  <img src={selectedIncident.image_url} alt="Evidencia" className="w-full h-auto max-h-[500px] object-contain mx-auto" />
                  <div className="absolute top-4 left-4 bg-black/50 text-white text-[10px] font-mono px-2 py-1 rounded backdrop-blur-sm">
                    {selectedIncident.timestamp} | {selectedIncident.camera_name}
                  </div>
                </div>
              </div>

              <div className="flex gap-4 pt-4 border-t">
                <Button className="flex-1 h-12">
                  <Download size={18} /> Descargar Imagen
                </Button>
                <Button variant="outline" className="flex-1 h-12">
                  <Check size={18} /> Marcar como Falsa Alarma
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Incidents;
