import { useEffect, useMemo, useState } from "react";
import { AdminSidebar } from "./components/AdminSidebar";
import { Topbar } from "./components/Topbar";
import { Dashboard } from "./components/Dashboard";
import { Cameras } from "./components/Cameras";
import { Incidents } from "./components/Incidents";
import { Evidence } from "./components/Evidence";
import { Alerts } from "./components/Alerts";
import { Settings } from "./components/Settings";
import { Toaster } from "./components/ui/sonner";
import { SidebarProvider, SidebarInset } from "./components/ui/sidebar";
import "./styles/globals.css";

// ✅ Tipos mínimos para contar
type AlertDoc = { read?: boolean };
type CameraDoc = unknown;
type IncidentDoc = unknown;

// ✅ Soporta Vite (import.meta.env) y CRA (process.env)
function getApiBase() {
  const vite = (import.meta as any)?.env?.VITE_API_URL;
  const cra = (process as any)?.env?.REACT_APP_API_BASE;
  return vite || cra || "http://localhost:8000/api/v1";
}

export default function App() {
  const [activeModule, setActiveModule] = useState("dashboard");

  // ✅ badges dinámicos
  const [badgeCounts, setBadgeCounts] = useState({
    cameras: 0,
    incidents: 0,
    alertsUnread: 0,
  });

  const API_BASE = useMemo(() => getApiBase(), []);

  const fetchCounts = async () => {
    try {
      const [camsRes, incRes, alertsRes] = await Promise.all([
        fetch(`${API_BASE}/cameras`),
        fetch(`${API_BASE}/incidents?limit=1000`),
        fetch(`${API_BASE}/alerts?limit=1000`),
      ]);

      if (!camsRes.ok || !incRes.ok || !alertsRes.ok) {
        // Si algo falla, no rompas la app
        console.warn("No se pudieron cargar badges:", {
          cams: camsRes.status,
          inc: incRes.status,
          alerts: alertsRes.status,
        });
        return;
      }

      const cameras: CameraDoc[] = await camsRes.json();
      const incidents: IncidentDoc[] = await incRes.json();
      const alerts: AlertDoc[] = await alertsRes.json();

      const alertsUnread = alerts.filter((a) => !a.read).length;

      setBadgeCounts({
        cameras: cameras.length,
        incidents: incidents.length,
        alertsUnread,
      });
    } catch (err) {
      console.warn("Error cargando badges:", err);
    }
  };

  // ✅ carga inicial + refresco cada 5s (puedes cambiarlo)
  useEffect(() => {
    fetchCounts();
    const t = setInterval(fetchCounts, 5000);
    return () => clearInterval(t);
  }, [API_BASE]);

  const renderContent = () => {
    switch (activeModule) {
      case "dashboard":
        return <Dashboard />;
      case "cameras":
        return <Cameras />;
      case "incidents":
        return <Incidents />;
      case "evidence":
        return <Evidence />;
      case "alerts":
        return <Alerts />;
      case "settings":
        return <Settings />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <SidebarProvider>
      <AdminSidebar
        activeModule={activeModule}
        onModuleChange={setActiveModule}
        badges={{
          cameras: badgeCounts.cameras,
          incidents: badgeCounts.incidents,
          alerts: badgeCounts.alertsUnread, // ✅ aquí usamos NO LEÍDAS
        }}
      />

      <SidebarInset>
        <div className="min-h-svh flex flex-col">
          <Topbar />
          <main className="flex-1 overflow-hidden">
            <div className="p-8 max-w-[1600px] mx-auto w-full h-full">
              {renderContent()}
            </div>
          </main>
        </div>
      </SidebarInset>

      <Toaster />
    </SidebarProvider>
  );
}
