import React, { useState, useMemo } from 'react';
import { 
  Eye, 
  Filter, 
  AlertTriangle, 
  Download, 
  Camera, 
  Calendar, 
  TrendingUp, 
  Shield,
  Search,
  ChevronRight,
  MoreHorizontal
} from 'lucide-react';

// --- Interfaces ---
interface Incident {
  id: string;
  weapon_type: string;
  confidence: number;
  timestamp: string;
  date: string;
  time: string;
  camera_id: string;
  camera_name: string;
  image_url: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
}

const mockIncidents: Incident[] = [
  { id: 'INC-001', weapon_type: 'Pistola', confidence: 0.94, timestamp: '2026-02-09 14:32:15', date: '09/02/2026', time: '14:32:15', camera_id: 'CAM-001', camera_name: 'Entrada Principal', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'high' },
  { id: 'INC-002', weapon_type: 'Rifle', confidence: 0.87, timestamp: '2026-02-09 13:18:42', date: '09/02/2026', time: '13:18:42', camera_id: 'CAM-005', camera_name: 'Estacionamiento Norte', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'critical' },
  { id: 'INC-003', weapon_type: 'Cuchillo', confidence: 0.76, timestamp: '2026-02-09 12:05:28', date: '09/02/2026', time: '12:05:28', camera_id: 'CAM-012', camera_name: 'Pasillo Norte - Piso 2', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'medium' },
  { id: 'INC-004', weapon_type: 'Pistola', confidence: 0.91, timestamp: '2026-02-09 11:44:11', date: '09/02/2026', time: '11:44:11', camera_id: 'CAM-003', camera_name: 'Recepción Principal', image_url: 'https://images.unsplash.com/photo-1557597774-9d273605dfa9?w=800', severity: 'high' },
];

export const Incidents = () => {
  const [filters, setFilters] = useState({
    search: '',
    camera: 'all',
    severity: 'all'
  });

  // Cálculo de cámaras únicas para el filtro
  const uniqueCameras = useMemo(() => {
    const map = new Map();
    mockIncidents.forEach(inc => {
      if (!map.has(inc.camera_id)) map.set(inc.camera_id, inc.camera_name);
    });
    return Array.from(map.entries());
  }, []);

  const getSeverityStyles = (severity: string) => {
    switch (severity) {
      case 'critical': return 'bg-red-100 text-red-700 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-700 border-orange-200';
      case 'medium': return 'bg-amber-100 text-amber-700 border-amber-200';
      default: return 'bg-slate-100 text-slate-700 border-slate-200';
    }
  };

  return (
    <div className="w-full space-y-6 animate-in fade-in duration-500">
      
      {/* 1. Header Responsivo */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h1 className="text-2xl md:text-3xl font-bold text-slate-900 tracking-tight">Detecciones Recientes</h1>
          <p className="text-slate-500 text-sm mt-1">Monitoreo de inteligencia artificial en tiempo real</p>
        </div>
        <div className="flex items-center gap-2">
          <button className="flex-1 md:flex-none inline-flex items-center justify-center gap-2 px-4 py-2 bg-white border border-slate-200 rounded-xl text-sm font-semibold text-slate-700 hover:bg-slate-50 transition-all shadow-sm">
            <Download size={18} /> Exportar
          </button>
          <button className="flex-1 md:flex-none inline-flex items-center justify-center gap-2 px-4 py-2 bg-blue-600 rounded-xl text-sm font-semibold text-white hover:bg-blue-700 transition-all shadow-md shadow-blue-100">
            <TrendingUp size={18} /> Reporte IA
          </button>
        </div>
      </div>

      {/* 2. Grid de Estadísticas (Se adapta de 1 a 4 columnas) */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {[
          { label: 'Total Hoy', val: '24', icon: AlertTriangle, color: 'text-blue-600', bg: 'bg-blue-50' },
          { label: 'Críticos', val: '03', icon: Shield, color: 'text-red-600', bg: 'bg-red-50' },
          { label: 'Cámaras', val: '12', icon: Camera, color: 'text-emerald-600', bg: 'bg-emerald-50' },
          { label: 'Confianza', val: '92%', icon: TrendingUp, color: 'text-purple-600', bg: 'bg-purple-50' },
        ].map((stat, i) => (
          <div key={i} className="bg-white p-5 rounded-2xl border border-slate-100 shadow-sm flex items-center gap-4 hover:shadow-md transition-shadow">
            <div className={`p-3 rounded-xl ${stat.bg} ${stat.color}`}>
              <stat.icon size={24} />
            </div>
            <div>
              <p className="text-xs font-bold text-slate-400 uppercase tracking-wider">{stat.label}</p>
              <h3 className="text-2xl font-bold text-slate-900">{stat.val}</h3>
            </div>
          </div>
        ))}
      </div>

      {/* 3. Filtros y Búsqueda */}
      <div className="bg-white p-4 rounded-2xl border border-slate-100 shadow-sm flex flex-col lg:flex-row gap-4">
        <div className="relative flex-1 group">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-blue-500 transition-colors" size={18} />
          <input 
            type="text" 
            placeholder="Buscar por ID, arma o cámara..." 
            className="w-full pl-10 pr-4 py-2.5 bg-slate-50 border-none rounded-xl text-sm focus:ring-2 focus:ring-blue-500/20 outline-none transition-all"
            onChange={(e) => setFilters({...filters, search: e.target.value})}
          />
        </div>
        <div className="flex flex-wrap items-center gap-3">
          <select 
            className="bg-slate-50 border-none rounded-xl px-4 py-2.5 text-sm font-medium text-slate-600 focus:ring-2 focus:ring-blue-500/20 outline-none cursor-pointer"
            onChange={(e) => setFilters({...filters, camera: e.target.value})}
          >
            <option value="all">Todas las Cámaras</option>
            {uniqueCameras.map(([id, name]) => (
              <option key={id} value={id}>{name}</option>
            ))}
          </select>
          <select 
            className="bg-slate-50 border-none rounded-xl px-4 py-2.5 text-sm font-medium text-slate-600 focus:ring-2 focus:ring-blue-500/20 outline-none cursor-pointer"
            onChange={(e) => setFilters({...filters, severity: e.target.value})}
          >
            <option value="all">Severidad</option>
            <option value="critical">Crítica</option>
            <option value="high">Alta</option>
            <option value="medium">Media</option>
          </select>
        </div>
      </div>

      {/* 4. Tabla Responsiva (Contenedor con scroll horizontal) */}
      <div className="bg-white rounded-2xl border border-slate-100 shadow-sm overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full text-left border-collapse min-w-[800px]">
            <thead>
              <tr className="bg-slate-50/50 border-b border-slate-100 text-slate-500 text-[10px] font-bold uppercase tracking-[0.15em]">
                <th className="px-6 py-4">Evento</th>
                <th className="px-6 py-4">Detección</th>
                <th className="px-6 py-4">Confianza</th>
                <th className="px-6 py-4">Ubicación / Cámara</th>
                <th className="px-6 py-4">Estado</th>
                <th className="px-6 py-4 text-right">Acción</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {mockIncidents.map((incident) => (
                <tr key={incident.id} className="hover:bg-slate-50/50 transition-colors group">
                  <td className="px-6 py-4">
                    <span className="font-mono font-bold text-slate-900 text-sm">{incident.id}</span>
                    <p className="text-[10px] text-slate-400 mt-0.5">{incident.date} · {incident.time}</p>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-2">
                      <div className={`w-2 h-2 rounded-full ${incident.severity === 'critical' ? 'bg-red-500 animate-pulse' : 'bg-blue-500'}`} />
                      <span className="text-sm font-bold text-slate-700">{incident.weapon_type}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-3">
                      <div className="flex-1 min-w-[60px] bg-slate-100 h-1.5 rounded-full overflow-hidden">
                        <div 
                          className={`h-full ${incident.confidence > 0.9 ? 'bg-emerald-500' : 'bg-blue-500'}`} 
                          style={{ width: `${incident.confidence * 100}%` }}
                        />
                      </div>
                      <span className="text-xs font-mono font-bold">{(incident.confidence * 100).toFixed(0)}%</span>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <span className="text-xs font-bold text-slate-600 block">{incident.camera_name}</span>
                    <span className="text-[10px] text-slate-400 uppercase tracking-tighter">{incident.camera_id}</span>
                  </td>
                  <td className="px-6 py-4">
                    <span className={`text-[10px] font-bold uppercase px-2 py-1 rounded-lg border ${getSeverityStyles(incident.severity)}`}>
                      {incident.severity}
                    </span>
                  </td>
                  <td className="px-6 py-4 text-right">
                    <button className="p-2 text-slate-400 hover:text-blue-600 hover:bg-blue-50 rounded-lg transition-all inline-flex items-center gap-1 text-xs font-bold uppercase">
                      <Eye size={16} /> <span className="hidden sm:inline">Ver</span>
                    </button>
                    <button className="p-2 text-slate-400 hover:text-slate-600 rounded-lg">
                      <MoreHorizontal size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        
        {/* Footer de la tabla / Paginación simple */}
        <div className="px-6 py-4 bg-slate-50/30 border-t border-slate-50 flex items-center justify-between">
          <p className="text-xs text-slate-400 font-medium">Mostrando {mockIncidents.length} resultados</p>
          <div className="flex gap-2">
            <button className="p-1.5 rounded-lg border bg-white text-slate-400 hover:text-slate-600 disabled:opacity-50" disabled>
              <ChevronRight size={16} className="rotate-180" />
            </button>
            <button className="p-1.5 rounded-lg border bg-white text-slate-400 hover:text-slate-600">
              <ChevronRight size={16} />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Incidents;
