import React, { createContext, useContext, useEffect, useMemo, useRef, useState } from "react";

type Severity = "critical" | "high" | "medium" | "low";

export interface AlertApi {
  _id: string;
  type?: string;
  title: string;
  message: string;
  severity: Severity;
  weapon_type?: string | null;
  confidence?: number | null;
  evidence_url?: string | null;
  camera_id?: string | null;
  timestamp?: string | null;
  read?: boolean;
}

type AlertsContextValue = {
  alerts: AlertApi[];
  unreadCount: number;
  refresh: () => Promise<void>;
  markAsRead: (id: string) => Promise<void>;
  markAllAsRead: () => Promise<void>;
  clearAll: () => Promise<void>;
};

const AlertsContext = createContext<AlertsContextValue | null>(null);

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8000/api/v1";
const WS_BASE = process.env.REACT_APP_WS_BASE || "ws://localhost:8000";

function normalizeAlert(a: any): AlertApi {
  return {
    _id: String(a._id ?? a.id),
    type: a.type ?? "ALERTA",
    title: a.title ?? "Detección",
    message: a.message ?? "",
    severity: (a.severity ?? "medium") as Severity,
    weapon_type: a.weapon_type ?? null,
    confidence: typeof a.confidence === "number" ? a.confidence : null,
    evidence_url: a.evidence_url ?? null,
    camera_id: a.camera_id ?? null,
    timestamp: a.timestamp ?? null,
    read: !!a.read,
  };
}

export function AlertsProvider({ children }: { children: React.ReactNode }) {
  const [alerts, setAlerts] = useState<AlertApi[]>([]);
  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimer = useRef<number | null>(null);

  const refresh = async () => {
    const res = await fetch(`${API_BASE}/alerts?limit=50`);
    if (!res.ok) throw new Error("No se pudo cargar alertas");
    const data = await res.json();
    setAlerts((Array.isArray(data) ? data : []).map(normalizeAlert));
  };

  const markAsRead = async (id: string) => {
    const res = await fetch(`${API_BASE}/alerts/${id}/read?read=true`, { method: "PATCH" });
    if (!res.ok) throw new Error("No se pudo marcar como leída");
    setAlerts((prev) => prev.map((a) => (a._id === id ? { ...a, read: true } : a)));
  };

  const markAllAsRead = async () => {
    const unread = alerts.filter((a) => !a.read);
    for (const a of unread) {
      // eslint-disable-next-line no-await-in-loop
      await fetch(`${API_BASE}/alerts/${a._id}/read?read=true`, { method: "PATCH" });
    }
    setAlerts((prev) => prev.map((a) => ({ ...a, read: true })));
  };

  const clearAll = async () => {
    const res = await fetch(`${API_BASE}/alerts`, { method: "DELETE" });
    if (!res.ok) throw new Error("No se pudo limpiar alertas");
    setAlerts([]);
  };

  const unreadCount = useMemo(() => alerts.filter((a) => !a.read).length, [alerts]);

  // Carga inicial
  useEffect(() => {
    refresh().catch(console.error);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // WebSocket: recibe eventos y refresca
  useEffect(() => {
    const connect = () => {
      // evita duplicar conexiones
      if (wsRef.current && (wsRef.current.readyState === WebSocket.OPEN || wsRef.current.readyState === WebSocket.CONNECTING)) {
        return;
      }

      const ws = new WebSocket(`${WS_BASE}/api/v1/ws`);
      wsRef.current = ws;

      ws.onopen = () => {
        // console.log("WS conectado");
      };

      ws.onmessage = (event) => {
        // Si tu backend manda JSON tipo {"type":"ALERTA", ...}
        try {
          const msg = JSON.parse(event.data);
          if (msg?.type === "ALERTA") {
            refresh().catch(console.error);
          }
        } catch {
          // ignora mensajes no JSON (ping/pong)
        }
      };

      ws.onclose = () => {
        // console.log("WS cerrado, reconectando...");
        if (reconnectTimer.current) window.clearTimeout(reconnectTimer.current);
        reconnectTimer.current = window.setTimeout(connect, 1500);
      };

      ws.onerror = () => {
        try {
          ws.close();
        } catch {}
      };
    };

    connect();

    return () => {
      if (reconnectTimer.current) window.clearTimeout(reconnectTimer.current);
      try {
        wsRef.current?.close();
      } catch {}
      wsRef.current = null;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <AlertsContext.Provider value={{ alerts, unreadCount, refresh, markAsRead, markAllAsRead, clearAll }}>
      {children}
    </AlertsContext.Provider>
  );
}

export function useAlerts() {
  const ctx = useContext(AlertsContext);
  if (!ctx) throw new Error("useAlerts debe usarse dentro de <AlertsProvider/>");
  return ctx;
}
