export function connectAlerts(onMessage: (data: any) => void) {
  const ws = new WebSocket("ws://localhost:8000/ws/alerts");

  ws.onmessage = (event) => {
    onMessage(JSON.parse(event.data));
  };

  ws.onerror = () => {
    console.error("WebSocket error");
  };

  return ws;
}
