// frontend/src/modules/settings/api/settingsService.ts

import { http } from "../../../api/http"; // ✅ ajusta el path si tu estructura es distinta

export type SystemSettings = {
  confidence_threshold: number; // 0.75
  auto_alert: boolean;
  email_notifications: boolean;
  sound_alerts: boolean;
  save_evidence: boolean;
  max_fps: number; // 30
  infer_every_n_frames: number; // 5
  updated_at?: string;
};

export async function getSettings(): Promise<SystemSettings> {
  const res = await http.get<SystemSettings>("/settings");
  return res.data;
}

/**
 * ✅ TU BACKEND ES PATCH /settings
 */
export async function updateSettings(
  payload: Partial<SystemSettings>
): Promise<SystemSettings> {
  const res = await http.patch<SystemSettings>("/settings", payload);
  return res.data;
}
