import { http } from "./http";

export type Role = "admin" | "operator";

export type UserOut = {
  _id: string;
  name: string;
  email: string;
  role: Role;
  estado?: number; // 1 activo, 0 inactivo
  created_at?: string | null;
};

export type UserCreate = {
  name: string;
  email: string;
  password: string;
  role: Role;
  estado?: 0 | 1;
};

export type UserUpdate = {
  name?: string;
  email?: string;
  password?: string;
  role?: Role;
  estado?: 0 | 1;
};

export async function listUsers(limit = 500) {
  const { data } = await http.get<UserOut[]>(`/users`, { params: { limit } });
  return data;
}

export async function createUser(payload: UserCreate) {
  const { data } = await http.post<UserOut>(`/users`, {
    ...payload,
    estado: payload.estado ?? 1,
  });
  return data;
}

export async function updateUser(userId: string, payload: UserUpdate) {
  const { data } = await http.patch<UserOut>(`/users/${userId}`, payload);
  return data;
}

export async function setUserRole(userId: string, role: Role) {
  // backend usa query param role
  const { data } = await http.patch<UserOut>(`/users/${userId}/role`, null, {
    params: { role },
  });
  return data;
}

export async function setUserEstado(userId: string, estado: 0 | 1) {
  // ✅ manda BODY (y backend también acepta query por si acaso)
  const { data } = await http.patch<UserOut>(`/users/${userId}/estado`, { estado });
  return data;
}

export async function deleteUser(userId: string) {
  const { data } = await http.delete(`/users/${userId}`);
  return data;
}
