import React, { useMemo, useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Switch } from "./ui/switch";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from "./ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "./ui/table";
import {
  Play,
  Square,
  Pencil,
  Trash2,
  Plus,
  Video,
  Activity,
  Server,
  ChevronLeft,
  ChevronRight,
  Eye,
} from "lucide-react";
import { toast } from "sonner";

interface Camera {
  id: string;
  name: string;
  rtsp_url: string;
  enabled: boolean;
  fps_target: number;
  infer_every_n_frames: number;
  status: "RUNNING" | "STOPPED";
}

const initialCameras: Camera[] = [
  {
    id: "0",
    name: "Webcam Local (Pruebas)",
    rtsp_url: "0",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "RUNNING",
  },
  {
    id: "CAM-002",
    name: "Estacionamiento Norte",
    rtsp_url: "rtsp://192.168.1.101:554/stream1",
    enabled: true,
    fps_target: 25,
    infer_every_n_frames: 10,
    status: "RUNNING",
  },
  {
    id: "CAM-003",
    name: "Recepci칩n Principal",
    rtsp_url: "rtsp://192.168.1.102:554/stream1",
    enabled: false,
    fps_target: 30,
    infer_every_n_frames: 5,
    status: "STOPPED",
  },
];

export function Cameras() {
  const [cameras, setCameras] = useState<Camera[]>(initialCameras);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [isVideoModalOpen, setIsVideoModalOpen] = useState(false);
  const [activeVideoCamera, setActiveVideoCamera] = useState<Camera | null>(null);
  const [editingCamera, setEditingCamera] = useState<Camera | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    rtsp_url: "",
    enabled: true,
    fps_target: 30,
    infer_every_n_frames: 5,
  });

  // PAGINACI칍N
  const pageSize = 5;
  const [page, setPage] = useState(1);
  const totalPages = Math.max(1, Math.ceil(cameras.length / pageSize));

  useEffect(() => {
    setPage((p) => Math.min(p, totalPages));
  }, [totalPages]);

  const pageData = useMemo(() => {
    const start = (page - 1) * pageSize;
    return cameras.slice(start, start + pageSize);
  }, [cameras, page]);

  const canPrev = page > 1;
  const canNext = page < totalPages;
  const goPrev = () => setPage((p) => Math.max(1, p - 1));
  const goNext = () => setPage((p) => Math.min(totalPages, p + 1));

  const handleStartStop = (id: string, currentStatus: string) => {
    setCameras((prev) =>
      prev.map((cam) =>
        cam.id === id
          ? { ...cam, status: currentStatus === "RUNNING" ? "STOPPED" : "RUNNING" }
          : cam
      )
    );
    toast.success(currentStatus === "RUNNING" ? "游댮 Detenida" : "游릭 Iniciada");
  };

  const handleDelete = (id: string, name: string) => {
    setCameras((prev) => prev.filter((cam) => cam.id !== id));
    toast.success(`C치mara "${name}" eliminada`);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingCamera) {
      setCameras((prev) => prev.map((cam) => cam.id === editingCamera.id ? { ...cam, ...formData } : cam));
    } else {
      const newCamera: Camera = {
        id: `CAM-${String(cameras.length + 1).padStart(3, "0")}`,
        ...formData,
        status: formData.enabled ? "RUNNING" : "STOPPED",
      };
      setCameras((prev) => [...prev, newCamera]);
    }
    setIsDialogOpen(false);
  };

  const runningCameras = cameras.filter((c) => c.status === "RUNNING").length;
  const stoppedCameras = cameras.filter((c) => c.status === "STOPPED").length;

  return (
    <div className="space-y-5">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-slate-900">Gesti칩n de C치maras</h1>
          <p className="text-sm text-slate-600">Administra tus dispositivos</p>
        </div>
        <Button onClick={() => { setEditingCamera(null); setIsDialogOpen(true); }}>
          <Plus className="h-5 w-5 mr-2" /> Nueva C치mara
        </Button>
      </div>

      {/* Stats Cards (Uso de Activity, runningCameras, stoppedCameras) */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card className="border-0 shadow-sm border-l-4 border-l-emerald-500">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">Activas</p>
                <p className="text-2xl font-bold text-emerald-600">{runningCameras}</p>
              </div>
              <Play className="h-5 w-5 text-emerald-600" />
            </div>
          </CardContent>
        </Card>
        <Card className="border-0 shadow-sm border-l-4 border-l-slate-400">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">Detenidas</p>
                <p className="text-2xl font-bold text-slate-600">{stoppedCameras}</p>
              </div>
              <Square className="h-5 w-5 text-slate-600" />
            </div>
          </CardContent>
        </Card>
        <Card className="border-0 shadow-sm border-l-4 border-l-blue-500">
          <CardContent className="pt-5 pb-5">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-xs font-semibold text-slate-600">Actividad</p>
                <p className="text-2xl font-bold text-blue-600">
                    {cameras.length > 0 ? Math.round((runningCameras / cameras.length) * 100) : 0}%
                </p>
              </div>
              <Activity className="h-5 w-5 text-blue-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      <Card className="border-0 shadow-sm">
        <CardContent className="p-0">
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>ID</TableHead>
                <TableHead>Nombre</TableHead>
                <TableHead>Estado</TableHead>
                <TableHead className="text-right">Acciones</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {pageData.map((camera) => (
                <TableRow key={camera.id}>
                  <TableCell className="font-mono">{camera.id}</TableCell>
                  <TableCell>{camera.name}</TableCell>
                  <TableCell>
                    <Badge className={camera.status === "RUNNING" ? "bg-emerald-100 text-emerald-700" : "bg-slate-100 text-slate-600"}>
                      {camera.status}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-right flex justify-end gap-2">
                    <Button size="sm" onClick={() => { setActiveVideoCamera(camera); setIsVideoModalOpen(true); }} disabled={camera.status === "STOPPED"}>
                      <Eye className="h-4 w-4 mr-1" /> Ver
                    </Button>
                    <Button size="sm" variant="outline" onClick={() => handleStartStop(camera.id, camera.status)}>
                      {camera.status === "RUNNING" ? <Square className="h-4 w-4" /> : <Play className="h-4 w-4" />}
                    </Button>
                    <Button size="sm" variant="outline" onClick={() => handleDelete(camera.id, camera.name)}>
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>

          {/* Paginaci칩n (Uso de canPrev, canNext, goPrev, goNext, ChevronLeft, ChevronRight) */}
          <div className="p-4 border-t flex items-center justify-between">
            <span className="text-xs text-slate-500">P치gina {page} de {totalPages}</span>
            <div className="flex gap-2">
              <Button variant="outline" size="sm" onClick={goPrev} disabled={!canPrev}>
                <ChevronLeft className="h-4 w-4" />
              </Button>
              <Button variant="outline" size="sm" onClick={goNext} disabled={!canNext}>
                <ChevronRight className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Modal de Video */}
      <Dialog open={isVideoModalOpen} onOpenChange={setIsVideoModalOpen}>
        <DialogContent className="sm:max-w-4xl bg-slate-950 border-slate-800">
          <DialogHeader>
            <DialogTitle className="text-slate-100 flex items-center gap-2">
                <div className="h-2 w-2 bg-rose-500 rounded-full animate-pulse" />
                VIVO: {activeVideoCamera?.name}
            </DialogTitle>
          </DialogHeader>
          <div className="aspect-video bg-black rounded-lg overflow-hidden border border-slate-800 mt-2">
            {activeVideoCamera && (
              <img
                src={`http://localhost:8000/api/v1/inference/stream/${activeVideoCamera.id}`}
                alt="Live Stream"
                className="w-full h-full object-contain"
              />
            )}
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}
