import { http } from "./http";

export type Incident = {
  _id?: string;
  id?: string;
  camera_id?: string;
  weapon_type?: string;
  confidence?: number;
  created_at?: string;

  // por si el backend usa otros nombres
  timestamp?: string;
  type?: string;
};

export async function listIncidents() {
  const { data } = await http.get<Incident[]>("/incidents");
  return data;
}

export async function deleteAllIncidents() {
  const { data } = await http.delete("/incidents");
  return data;
}
